import { type Battle } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Clock, Users } from "lucide-react";

interface BattleCardProps {
  battle: Battle;
  featured?: boolean;
  onVote?: (battleId: string, contestantId: string) => void;
  isVoting?: boolean;
}

export default function BattleCard({ battle, featured = false, onVote, isVoting = false }: BattleCardProps) {
  const getPercentages = () => {
    const total = battle.totalVotes || 0;
    if (total === 0) return { contestant1: 50, contestant2: 50 };

    const contestant1Percent = Math.round(((battle.contestant1Votes || 0) / total) * 100);
    const contestant2Percent = 100 - contestant1Percent;

    return { contestant1: contestant1Percent, contestant2: contestant2Percent };
  };

  const percentages = getPercentages();

  const getStatusColor = () => {
    switch (battle.status) {
      case 'active': return 'bg-success-500';
      case 'completed': return 'bg-gray-500';
      default: return 'bg-highlight-500';
    }
  };

  const getCategoryColor = () => {
    switch (battle.category) {
      case 'freestyle': return 'text-highlight-500';
      case 'championship': return 'text-gold-500';
      case 'team': return 'text-success-500';
      case 'open_mic': return 'text-purple-500';
      default: return 'text-purple-500';
    }
  };

  return (
    <Card className={`bg-dark-200 border-dark-400 ${featured ? 'p-8' : ''}`} data-testid={`battle-card-${battle.id}`}>
      <CardContent className={featured ? 'p-0' : 'p-6'}>
        {featured && (
          <div className="text-center mb-8">
            <h3 className="text-2xl font-bold text-white mb-4" data-testid={`battle-title-${battle.id}`}>
              {battle.title}
            </h3>
            <div className="flex items-center justify-center space-x-4 text-sm text-gray-400 mb-4">
              <Badge className={`${getCategoryColor()} bg-transparent border`} data-testid={`battle-category-${battle.id}`}>
                {battle.category.replace('_', ' ').toUpperCase()}
              </Badge>
              <span className="flex items-center">
                <Users className="w-4 h-4 mr-1" />
                {battle.totalVotes?.toLocaleString() || 0} votes
              </span>
              {battle.endTime && (
                <span className="flex items-center">
                  <Clock className="w-4 h-4 mr-1" />
                  Ends {new Date(battle.endTime).toLocaleDateString()}
                </span>
              )}
            </div>
          </div>
        )}

        <div className={`grid ${featured ? 'grid-cols-3' : 'grid-cols-1 md:grid-cols-3'} gap-8 items-center`}>
          {/* Contestant 1 */}
          <div className="text-center lg:text-right">
            <div className="inline-block mb-4">
              <div className="w-24 h-24 bg-gradient-to-br from-purple-500 to-purple-600 rounded-full border-4 border-purple-500 flex items-center justify-center text-xl font-bold text-white">
                {battle.contestant1Name.split(' ').map(n => n[0]).join('')}
              </div>
            </div>
            <h4 className="text-2xl font-bold mb-2 text-white" data-testid={`contestant1-name-${battle.id}`}>
              {battle.contestant1Name}
            </h4>
            <p className="text-gray-400 mb-4">The Lightning Strike</p>
            <div className="bg-purple-500 rounded-lg p-4">
              <div className="text-3xl font-black mb-2 text-white" data-testid={`contestant1-percentage-${battle.id}`}>
                {percentages.contestant1}%
              </div>
              {onVote && battle.status === 'active' && (
                <Button 
                  onClick={() => onVote(battle.id, battle.contestant1Id)}
                  disabled={isVoting}
                  className="w-full bg-white bg-opacity-20 hover:bg-opacity-30 text-white font-medium transition-all"
                  data-testid={`button-vote-contestant1-${battle.id}`}
                >
                  {isVoting ? 'Voting...' : `Vote ${battle.contestant1Name.split(' ')[0]}`}
                </Button>
              )}
            </div>
          </div>

          {/* VS Section */}
          <div className="text-center">
            <div className="text-6xl font-black text-gray-600 mb-4">VS</div>
            <div className="bg-dark-300 rounded-lg p-4">
              <div className="text-sm text-gray-400 mb-2">Battle Status</div>
              <div className={`font-bold ${getCategoryColor()}`} data-testid={`battle-status-${battle.id}`}>
                {battle.status === 'active' ? 'ACTIVE' : (battle.status || 'active').toUpperCase()}
              </div>
              {battle.endTime && battle.status === 'active' && (
                <div className="text-sm text-gray-400 mt-2">
                  Ends {new Date(battle.endTime).toLocaleDateString()}
                </div>
              )}
            </div>
            <Button 
              className="mt-4 bg-gradient-to-r from-purple-500 to-electric-500 text-white font-bold hover:from-purple-600 hover:to-electric-600 transition-all"
              data-testid={`button-watch-battle-${battle.id}`}
            >
              Watch Battle
            </Button>
          </div>

          {/* Contestant 2 */}
          <div className="text-center lg:text-left">
            <div className="inline-block mb-4">
              <div className="w-24 h-24 bg-gradient-to-br from-electric-500 to-electric-600 rounded-full border-4 border-electric-500 flex items-center justify-center text-xl font-bold text-white">
                {battle.contestant2Name.split(' ').map(n => n[0]).join('')}
              </div>
            </div>
            <h4 className="text-2xl font-bold mb-2 text-white" data-testid={`contestant2-name-${battle.id}`}>
              {battle.contestant2Name}
            </h4>
            <p className="text-gray-400 mb-4">Wordplay Royalty</p>
            <div className="bg-electric-500 rounded-lg p-4">
              <div className="text-3xl font-black mb-2 text-white" data-testid={`contestant2-percentage-${battle.id}`}>
                {percentages.contestant2}%
              </div>
              {onVote && battle.status === 'active' && (
                <Button 
                  onClick={() => onVote(battle.id, battle.contestant2Id)}
                  disabled={isVoting}
                  className="w-full bg-white bg-opacity-20 hover:bg-opacity-30 text-white font-medium transition-all"
                  data-testid={`button-vote-contestant2-${battle.id}`}
                >
                  {isVoting ? 'Voting...' : `Vote ${battle.contestant2Name.split(' ')[0]}`}
                </Button>
              )}
            </div>
          </div>
        </div>

        {!featured && (
          <div className="mt-6 pt-4 border-t border-dark-400">
            <div className="flex items-center justify-between text-sm">
              <div className="flex items-center space-x-4 text-gray-400">
                <span data-testid={`battle-votes-${battle.id}`}>
                  {battle.totalVotes?.toLocaleString() || 0} votes
                </span>
                <span data-testid={`battle-views-${battle.id}`}>
                  {battle.views?.toLocaleString() || 0} views
                </span>
              </div>
              <Badge 
                className={`${getStatusColor()} text-white`}
                data-testid={`battle-status-badge-${battle.id}`}
              >
                {battle.status?.toUpperCase()}
              </Badge>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/beat-card.tsx.content:
import { type Beat } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Play, Heart, ShoppingCart, Eye } from "lucide-react";

interface BeatCardProps {
  beat: Beat;
  onPlay?: () => void;
  onLike?: () => void;
  onPurchase?: () => void;
  isPlaying?: boolean;
  isPurchasing?: boolean;
}

export default function BeatCard({ 
  beat, 
  onPlay, 
  onLike, 
  onPurchase, 
  isPlaying = false, 
  isPurchasing = false 
}: BeatCardProps) {

  const getGenreColor = () => {
    switch (beat.genre?.toLowerCase()) {
      case 'trap': return 'bg-highlight-500';
      case 'boom bap': return 'bg-purple-500';
      case 'drill': return 'bg-electric-500';
      case 'r&b': return 'bg-success-500';
      case 'lo-fi': return 'bg-gray-500';
      default: return 'bg-purple-500';
    }
  };

  const getLicenseColor = () => {
    switch (beat.licenseType?.toLowerCase()) {
      case 'basic': return 'text-gray-400';
      case 'premium': return 'text-highlight-500';
      case 'exclusive': return 'text-gold-500';
      default: return 'text-gray-400';
    }
  };

  return (
    <Card className="bg-dark-200 border-dark-400 hover:bg-dark-300 transition-colors group" data-testid={`beat-card-${beat.id}`}>
      <CardContent className="p-0">
        {/* Cover Image / Thumbnail */}
        <div className="aspect-video bg-gradient-to-br from-purple-500 to-electric-500 relative overflow-hidden">
          {beat.coverImage ? (
            <img 
              src={beat.coverImage} 
              alt={beat.title}
              className="w-full h-full object-cover"
            />
          ) : (
            <div className="w-full h-full bg-gradient-to-br from-purple-500 to-electric-500 flex items-center justify-center">
              <Play className="h-16 w-16 text-white opacity-50" />
            </div>
          )}

          <div className="absolute inset-0 bg-black bg-opacity-50"></div>

          <div className="absolute top-4 left-4">
            <Badge className={`${getGenreColor()} text-white font-medium`} data-testid={`beat-genre-${beat.id}`}>
              {beat.genre?.toUpperCase()}
            </Badge>
          </div>

          <div className="absolute top-4 right-4">
            <Badge variant="secondary" className="bg-black bg-opacity-60 text-white" data-testid={`beat-bpm-${beat.id}`}>
              {beat.bpm} BPM
            </Badge>
          </div>

          <div className="absolute inset-0 flex items-center justify-center">
            <Button
              onClick={onPlay}
              className={`w-16 h-16 ${getGenreColor()} bg-opacity-80 rounded-full flex items-center justify-center hover:bg-opacity-90 transition-all ${isPlaying ? 'animate-pulse' : ''}`}
              data-testid={`button-play-beat-${beat.id}`}
            >
              <Play className="text-white text-xl ml-1" />
            </Button>
          </div>
        </div>

        {/* Beat Information */}
        <div className="p-4">
          <h3 className="font-bold text-lg text-white mb-2" data-testid={`beat-title-${beat.id}`}>
            {beat.title}
          </h3>
          <p className="text-gray-400 text-sm mb-3" data-testid={`beat-producer-${beat.id}`}>
            by @{beat.producerName}
          </p>

          {/* Stats */}
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-4 text-gray-400 text-sm">
              <span className="flex items-center" data-testid={`beat-plays-${beat.id}`}>
                <Play className="w-4 h-4 mr-1" />
                {beat.plays?.toLocaleString() || '0'}
              </span>
              <button 
                onClick={onLike}
                className="flex items-center hover:text-red-500 transition-colors"
                data-testid={`button-like-beat-${beat.id}`}
              >
                <Heart className="w-4 h-4 mr-1" />
                {beat.likes?.toLocaleString() || '0'}
              </button>
            </div>
            <div className="text-right">
              <div className="text-xl font-bold text-success-500" data-testid={`beat-price-${beat.id}`}>
                ${beat.price}
              </div>
              <div className={`text-xs ${getLicenseColor()}`} data-testid={`beat-license-${beat.id}`}>
                {beat.licenseType?.charAt(0).toUpperCase()}{beat.licenseType?.slice(1)} License
              </div>
            </div>
          </div>

          {/* Tags */}
          {beat.tags && beat.tags.length > 0 && (
            <div className="flex flex-wrap gap-1 mb-4">
              {beat.tags.slice(0, 3).map((tag, index) => (
                <Badge 
                  key={index} 
                  variant="outline" 
                  className="border-dark-400 text-gray-400 text-xs"
                  data-testid={`beat-tag-${beat.id}-${index}`}
                >
                  #{tag}
                </Badge>
              ))}
            </div>
          )}

          {/* Actions */}
          <div className="flex space-x-2">
            <Button 
              onClick={onPurchase}
              disabled={isPurchasing || !beat.isAvailable}
              className="flex-1 bg-success-500 hover:bg-success-600 text-white font-medium transition-colors disabled:opacity-50"
              data-testid={`button-purchase-beat-${beat.id}`}
            >
              {isPurchasing ? 'Processing...' : beat.isAvailable ? 'Buy License' : 'Sold Out'}
            </Button>
            <Button 
              variant="outline"
              className="bg-dark-300 hover:bg-dark-400 text-gray-300 border-dark-400 px-3 transition-colors"
              data-testid={`button-cart-beat-${beat.id}`}
            >
              <ShoppingCart className="w-4 h-4" />
            </Button>
          </div>

          {/* Purchase count */}
          {beat.purchases && beat.purchases > 0 && (
            <div className="mt-3 text-xs text-gray-400 text-center" data-testid={`beat-purchases-${beat.id}`}>
              {beat.purchases} license{beat.purchases !== 1 ? 's' : ''} sold
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/mixing-interface.t.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { type Track, type Collaboration } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Slider } from "@/components/ui/slider";
import { Badge } from "@/components/ui/badge";
import { 
  ArrowLeft, 
  Play, 
  Pause, 
  Square, 
  Volume2, 
  Mic, 
  Music, 
  Plus, 
  Undo, 
  Redo, 
  Save,
  Download,
  Share2,
  Users
} from "lucide-react";
import WaveformVisualizer from "./waveform-visualizer";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface MixingInterfaceProps {
  projectId: string;
  onBack: () => void;
}

interface MixingTrack {
  id: string;
  name: string;
  artist: string;
  type: 'beat' | 'vocals' | 'instrument';
  volume: number;
  pan: number;
  muted: boolean;
  solo: boolean;
  effects: string[];
  audioUrl?: string;
  waveformData: number[];
}

export default function MixingInterface({ projectId, onBack }: MixingInterfaceProps) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [masterVolume, setMasterVolume] = useState([75]);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(180); // 3 minutes default
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Mock mixing tracks - in real app, fetch from project data
  const [tracks, setTracks] = useState<MixingTrack[]>([
    {
      id: '1',
      name: 'Hip Hop Beat 120 BPM',
      artist: '@BeatMaster',
      type: 'beat',
      volume: 80,
      pan: 0,
      muted: false,
      solo: false,
      effects: [],
      waveformData: Array.from({length: 200}, () => Math.random() * 100)
    },
    {
      id: '2',
      name: 'Main Vocals - Verse 1',
      artist: '@MCThunder',
      type: 'vocals',
      volume: 75,
      pan: 0,
      muted: false,
      solo: false,
      effects: ['Reverb'],
      waveformData: Array.from({length: 200}, () => Math.random() * 80)
    }
  ]);

  const [selectedEffects, setSelectedEffects] = useState<string[]>(['Reverb']);
  const availableEffects = ['Reverb', 'Delay', 'Chorus', 'Auto-Tune', 'Distortion', 'EQ'];

  const { data: collaborations } = useQuery<Collaboration[]>({
    queryKey: ["/api/collaborations", { trackId: projectId }],
  });

  const saveMutation = useMutation({
    mutationFn: async () => {
      // In real implementation, save mixing state
      return new Promise(resolve => setTimeout(resolve, 1000));
    },
    onSuccess: () => {
      toast({
        title: "Project saved!",
        description: "Your mixing session has been saved.",
      });
    },
  });

  const exportMutation = useMutation({
    mutationFn: async () => {
      // In real implementation, export mixed audio
      return new Promise(resolve => setTimeout(resolve, 2000));
    },
    onSuccess: () => {
      toast({
        title: "Export complete!",
        description: "Your mixed track is ready for download.",
      });
    },
  });

  const updateTrack = (trackId: string, updates: Partial<MixingTrack>) => {
    setTracks(prev => prev.map(track => 
      track.id === trackId ? { ...track, ...updates } : track
    ));
  };

  const toggleEffect = (effect: string) => {
    setSelectedEffects(prev => 
      prev.includes(effect) 
        ? prev.filter(e => e !== effect)
        : [...prev, effect]
    );
  };

  const addNewTrack = () => {
    const newTrack: MixingTrack = {
      id: Date.now().toString(),
      name: 'New Layer',
      artist: '@You',
      type: 'vocals',
      volume: 75,
      pan: 0,
      muted: false,
      solo: false,
      effects: [],
      waveformData: Array.from({length: 200}, () => Math.random() * 60)
    };
    setTracks(prev => [...prev, newTrack]);
  };

  const getTrackIcon = (type: string) => {
    switch (type) {
      case 'beat': return Music;
      case 'vocals': return Mic;
      case 'instrument': return Music;
      default: return Music;
    }
  };

  const getTrackColor = (type: string) => {
    switch (type) {
      case 'beat': return 'bg-highlight-500';
      case 'vocals': return 'bg-purple-500';
      case 'instrument': return 'bg-electric-500';
      default: return 'bg-gray-500';
    }
  };

  // Simulate playback timer
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isPlaying) {
      interval = setInterval(() => {
        setCurrentTime(prev => {
          if (prev >= duration) {
            setIsPlaying(false);
            return 0;
          }
          return prev + 0.1;
        });
      }, 100);
    }
    return () => clearInterval(interval);
  }, [isPlaying, duration]);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="min-h-screen bg-dark-100">
      {/* Header */}
      <div className="bg-dark-200 border-b border-dark-400 p-4">
        <div className="max-w-7xl mx-auto flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button 
              variant="ghost" 
              onClick={onBack}
              className="text-gray-400 hover:text-white"
              data-testid="button-back-to-mixing"
            >
              <ArrowLeft className="w-5 h-5 mr-2" />
              Back to Studio
            </Button>
            <div>
              <h1 className="text-xl font-bold text-white" data-testid="project-title">
                {projectId === 'community-cypher' ? 'City Lights Cypher' : 'Street Symphony'}
              </h1>
              <p className="text-gray-400 text-sm">Collaborative Mix Session</p>
            </div>
          </div>

          {/* Transport Controls */}
          <div className="flex items-center space-x-4">
            <div className="text-sm text-gray-400 font-mono" data-testid="playback-time">
              {formatTime(currentTime)} / {formatTime(duration)}
            </div>
            <Button
              variant="outline"
              size="sm"
              onClick={() => setIsPlaying(!isPlaying)}
              className="border-success-500 text-success-500 hover:bg-success-500 hover:text-white"
              data-testid="button-play-pause"
            >
              {isPlaying ? <Pause className="w-4 h-4" /> : <Play className="w-4 h-4" />}
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => {
                setIsPlaying(false);
                setCurrentTime(0);
              }}
              className="border-red-500 text-red-500 hover:bg-red-500 hover:text-white"
              data-testid="button-stop"
            >
              <Square className="w-4 h-4" />
            </Button>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto p-6">
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {/* Track Layers - Main Area */}
          <div className="lg:col-span-3">
            <Card className="bg-dark-200 border-dark-400">
              <CardHeader>
                <CardTitle className="flex items-center justify-between text-white">
                  <span>Track Layers</span>
                  <Button
                    onClick={addNewTrack}
                    className="bg-success-500 hover:bg-success-600 text-white"
                    data-testid="button-add-track"
                  >
                    <Plus className="w-4 h-4 mr-2" />
                    Add Layer
                  </Button>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {tracks.map((track) => {
                  const TrackIcon = getTrackIcon(track.type);

                  return (
                    <div key={track.id} className="bg-dark-300 rounded-lg p-4" data-testid={`track-${track.id}`}>
                      {/* Track Header */}
                      <div className="flex items-center justify-between mb-3">
                        <div className="flex items-center space-x-3">
                          <div className={`w-8 h-8 ${getTrackColor(track.type)} rounded-full flex items-center justify-center`}>
                            <TrackIcon className="text-sm text-white" />
                          </div>
                          <div>
                            <div className="font-medium text-white" data-testid={`track-name-${track.id}`}>
                              {track.name}
                            </div>
                            <div className="text-sm text-gray-400" data-testid={`track-artist-${track.id}`}>
                              {track.artist}
                            </div>
                          </div>
                        </div>
                        <div className="flex items-center space-x-2">
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => updateTrack(track.id, { muted: !track.muted })}
                            className={track.muted ? "text-red-500" : "text-gray-400 hover:text-white"}
                            data-testid={`button-mute-${track.id}`}
                          >
                            M
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => updateTrack(track.id, { solo: !track.solo })}
                            className={track.solo ? "text-yellow-500" : "text-gray-400 hover:text-white"}
                            data-testid={`button-solo-${track.id}`}
                          >
                            S
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            className="text-success-500 hover:text-success-400"
                            data-testid={`button-play-track-${track.id}`}
                          >
                            <Play className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>

                      {/* Waveform */}
                      <div className="mb-4">
                        <WaveformVisualizer 
                          data={track.waveformData} 
                          color={getTrackColor(track.type).replace('bg-', '')}
                          isPlaying={isPlaying}
                          currentTime={currentTime}
                          duration={duration}
                        />
                      </div>

                      {/* Track Controls */}
                      <div className="grid grid-cols-2 gap-4">
                        <div>
                          <label className="text-xs text-gray-400 mb-2 block">Volume</label>
                          <div className="flex items-center space-x-2">
                            <Volume2 className="w-4 h-4 text-gray-400" />
                            <Slider
                              value={[track.volume]}
                              onValueChange={([value]) => updateTrack(track.id, { volume: value })}
                              max={100}
                              step={1}
                              className="flex-1"
                              data-testid={`slider-volume-${track.id}`}
