import { 
  Brain, TrendingUp, Clock, Heart, Users, 
  Star, Zap, Music, Headphones, Play,
  ThumbsUp, ThumbsDown, Shuffle, Target,
  Sparkles, Filter, Search, MoreHorizontal
} from "lucide-react";

interface AIRecommendation {
  id: string;
  type: 'track' | 'artist' | 'beat' | 'collaboration' | 'battle';
  title: string;
  artist: string;
  thumbnail: string;
  confidence: number;
  reason: string;
  tags: string[];
  metadata: {
    duration?: number;
    bpm?: number;
    genre?: string;
    mood?: string;
    energy?: number;
    danceability?: number;
    valence?: number;
  };
  engagement: {
    likes: number;
    views: number;
    shares: number;
  };
  isLiked: boolean;
  isPlaying: boolean;
}

interface TrendingTopic {
  id: string;
  name: string;
  type: 'hashtag' | 'challenge' | 'genre' | 'mood';
  growth: number;
  posts: number;
  participants: number;
  description: string;
}

interface UserPreferences {
  genres: string[];
  moods: string[];
  bpmRange: [number, number];
  energy: number;
  danceability: number;
  valence: number;
  discovery: number; // How adventurous the user wants recommendations
}

interface AIDiscoveryProps {
  onRecommendationInteraction?: (id: string, action: 'like' | 'dislike' | 'play' | 'share') => void;
  onPreferencesUpdate?: (preferences: UserPreferences) => void;
