      </div>

      {/* Main content */}
      <div className="absolute inset-0 flex flex-col items-center justify-center text-white">
        {/* Beat artwork */}
        <motion.div
          key={currentBeat.id}
          initial={{ scale: 0.8, opacity: 0 }}
