      {/* Navigation hints */}
      <div className="absolute top-1/2 left-4 transform -translate-y-1/2 text-white/50 text-xs">
        â†“ Next
      </div>
      <div className="absolute top-1/2 right-4 transform -translate-y-1/2 text-white/50 text-xs">
