import { useState, useRef, useCallback, useEffect } from "react";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Slider } from "@/components/ui/slider";
import { 
  Camera, CameraOff, Mic, MicOff, RotateCw, 
  Play, Pause, Square, Download, Upload,
  Sparkles, Music, Volume2, Timer, Zap,
  Filter, Palette, Mic2, Radio
} from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

interface AudioEffect {
  id: string;
  name: string;
  icon: string;
  active: boolean;
}

interface Beat {
  id: string;
  name: string;
  producer: string;
  bpm: number;
  genre: string;
  preview: string;
  price: number;
  trending: boolean;
}

interface InstantCreatorProps {
  onClose?: () => void;
  onPublish?: (recording: any) => void;
}

export default function InstantCreator({ onClose, onPublish }: InstantCreatorProps) {
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [cameraMode, setCameraMode] = useState<'front' | 'back'>('front');
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [selectedBeat, setSelectedBeat] = useState<Beat | null>(null);
  const [beatVolume, setBeatVolume] = useState([70]);
  const [micVolume, setMicVolume] = useState([80]);
  const [showEffects, setShowEffects] = useState(false);
  const [showBeats, setShowBeats] = useState(false);
  const [countdown, setCountdown] = useState(0);
  const [recordingProgress, setRecordingProgress] = useState(0);

  const videoRef = useRef<HTMLVideoElement>(null);
  const streamRef = useRef<MediaStream | null>(null);
  const recordingRef = useRef<MediaRecorder | null>(null);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  // Audio effects
  const [audioEffects, setAudioEffects] = useState<AudioEffect[]>([
    { id: 'autotune', name: 'Auto-Tune', icon: 'ðŸŽµ', active: false },
    { id: 'reverb', name: 'Reverb', icon: 'ðŸŒŠ', active: false },
    { id: 'echo', name: 'Echo', icon: 'ðŸ“¢', active: false },
    { id: 'distortion', name: 'Distortion', icon: 'âš¡', active: false },
    { id: 'pitch-up', name: 'Pitch Up', icon: 'â¬†ï¸', active: false },
    { id: 'pitch-down', name: 'Pitch Down', icon: 'â¬‡ï¸', active: false }
  ]);

  // Mock trending beats
  const [trendingBeats] = useState<Beat[]>([
    { id: '1', name: 'Midnight Vibes', producer: 'BeatMaster', bpm: 140, genre: 'Trap', preview: '', price: 0, trending: true },
    { id: '2', name: 'Street Dreams', producer: 'ProducerX', bpm: 128, genre: 'Drill', preview: '', price: 5, trending: true },
    { id: '3', name: 'City Lights', producer: 'WaveKing', bpm: 120, genre: 'R&B', preview: '', price: 0, trending: false },
    { id: '4', name: 'Boss Mode', producer: 'TrapLord', bpm: 150, genre: 'Trap', preview: '', price: 10, trending: true },
    { id: '5', name: 'Smooth Operator', producer: 'SoulBeats', bpm: 90, genre: 'Hip-Hop', preview: '', price: 0, trending: false }
  ]);

  // Initialize camera
  useEffect(() => {
    startCamera();
    return () => {
      stopCamera();
    };
  }, [cameraMode]);

  const startCamera = async () => {
    try {
      const constraints = {
        video: {
          facingMode: cameraMode === 'front' ? 'user' : 'environment',
          width: { ideal: 720 },
          height: { ideal: 1280 }
        },
        audio: true
      };

      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    } catch (error) {
      console.error('Camera access failed:', error);
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
  };

  // Recording controls
  const startCountdown = useCallback(() => {
    setCountdown(3);
    const countdownInterval = setInterval(() => {
      setCountdown(prev => {
        if (prev <= 1) {
          clearInterval(countdownInterval);
          startRecording();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  }, []);

  const startRecording = useCallback(() => {
    if (!streamRef.current) return;

    try {
      const mediaRecorder = new MediaRecorder(streamRef.current);
      recordingRef.current = mediaRecorder;

      const chunks: BlobPart[] = [];
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunks.push(event.data);
        }
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'video/mp4' });
        // Handle recording blob
        console.log('Recording completed:', blob);
      };

      mediaRecorder.start();
      setIsRecording(true);
      setRecordingTime(0);

      // Start timer
      timerRef.current = setInterval(() => {
        setRecordingTime(prev => {
          const newTime = prev + 1;
          setRecordingProgress((newTime / 60) * 100); // 60 second max

          // Auto-stop at 60 seconds
          if (newTime >= 60) {
            stopRecording();
            return 60;
          }
          return newTime;
        });
      }, 1000);

    } catch (error) {
      console.error('Recording failed:', error);
    }
  }, []);

  const stopRecording = useCallback(() => {
    if (recordingRef.current && isRecording) {
      recordingRef.current.stop();
      recordingRef.current = null;
    }

    if (timerRef.current) {
      clearInterval(timerRef.current);
      timerRef.current = null;
    }

    setIsRecording(false);
    setIsPaused(false);
    setRecordingProgress(0);
  }, [isRecording]);

  const togglePause = useCallback(() => {
    if (recordingRef.current) {
      if (isPaused) {
        recordingRef.current.resume();
        // Resume timer
        timerRef.current = setInterval(() => {
          setRecordingTime(prev => {
            const newTime = prev + 1;
            setRecordingProgress((newTime / 60) * 100);
            if (newTime >= 60) {
              stopRecording();
              return 60;
            }
            return newTime;
          });
        }, 1000);
      } else {
        recordingRef.current.pause();
        if (timerRef.current) {
          clearInterval(timerRef.current);
          timerRef.current = null;
        }
      }
      setIsPaused(!isPaused);
    }
  }, [isPaused, stopRecording]);

  const switchCamera = () => {
    setCameraMode(prev => prev === 'front' ? 'back' : 'front');
  };

  const toggleEffect = (effectId: string) => {
    setAudioEffects(prev => prev.map(effect => 
      effect.id === effectId 
        ? { ...effect, active: !effect.active }
        : effect
    ));
  };

  const selectBeat = (beat: Beat) => {
    setSelectedBeat(beat);
    setShowBeats(false);
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="h-screen w-full bg-black relative overflow-hidden">
      {/* Camera view */}
      <video
        ref={videoRef}
        autoPlay
        muted
        playsInline
        className={`absolute inset-0 w-full h-full object-cover ${
          !isVideoEnabled ? 'hidden' : ''
        }`}
      />

      {/* No video overlay */}
      {!isVideoEnabled && (
        <div className="absolute inset-0 bg-gradient-to-br from-purple-900 via-blue-900 to-black flex items-center justify-center">
          <div className="text-center text-white">
            <Radio className="w-16 h-16 mx-auto mb-4" />
            <div className="text-xl font-semibold">Audio Only Mode</div>
            <div className="text-gray-300">Voice recording active</div>
          </div>
        </div>
      )}

      {/* Countdown overlay */}
      <AnimatePresence>
        {countdown > 0 && (
          <motion.div
            initial={{ scale: 0, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0, opacity: 0 }}
            className="absolute inset-0 bg-black/50 flex items-center justify-center z-50"
          >
            <motion.div
              key={countdown}
              initial={{ scale: 2, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0, opacity: 0 }}
              className="text-white text-8xl font-bold"
            >
              {countdown}
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Top controls */}
      <div className="absolute top-4 left-4 right-4 z-40">
        <div className="flex items-center justify-between">
          <Button
            variant="ghost"
            onClick={onClose}
            className="text-white bg-black/50 rounded-full"
          >
            Ã—
          </Button>

          <div className="flex items-center space-x-2">
            {selectedBeat && (
              <Badge className="bg-purple-500 text-white px-3 py-1">
                ðŸŽµ {selectedBeat.name}
              </Badge>
            )}
            {audioEffects.some(e => e.active) && (
              <Badge className="bg-blue-500 text-white px-3 py-1">
                <Sparkles className="w-3 h-3 mr-1" />
                Effects
              </Badge>
            )}
          </div>

          <Button
            variant="ghost"
            onClick={switchCamera}
            className="text-white bg-black/50 rounded-full"
            disabled={!isVideoEnabled}
          >
            <RotateCw className="w-5 h-5" />
          </Button>
        </div>
      </div>

      {/* Recording progress */}
      {isRecording && (
        <div className="absolute top-16 left-4 right-4 z-40">
          <div className="bg-black/50 rounded-full p-2">
            <div className="flex items-center justify-between text-white text-sm mb-1">
              <span className="flex items-center">
                <div className="w-2 h-2 bg-red-500 rounded-full mr-2 animate-pulse" />
                {formatTime(recordingTime)}
              </span>
              <span>{formatTime(60 - recordingTime)} left</span>
            </div>
            <div className="w-full bg-gray-700 rounded-full h-1">
              <div 
                className="bg-red-500 h-1 rounded-full transition-all"
                style={{ width: `${recordingProgress}%` }}
              />
            </div>
          </div>
        </div>
      )}

      {/* Side effects panel */}
      <AnimatePresence>
        {showEffects && (
          <motion.div
            initial={{ x: '100%' }}
            animate={{ x: 0 }}
            exit={{ x: '100%' }}
            className="absolute right-0 top-0 w-64 h-full bg-black/80 backdrop-blur-sm z-30 p-4"
          >
            <div className="text-white">
              <h3 className="font-semibold mb-4">Audio Effects</h3>

              <div className="space-y-3 mb-6">
                {audioEffects.map(effect => (
                  <Button
                    key={effect.id}
                    variant={effect.active ? "default" : "outline"}
                    className={`w-full justify-start ${
                      effect.active ? 'bg-purple-500' : 'border-gray-600'
                    }`}
                    onClick={() => toggleEffect(effect.id)}
                  >
                    <span className="mr-2">{effect.icon}</span>
                    {effect.name}
                  </Button>
                ))}
              </div>

              <div className="space-y-4">
                <div>
                  <label className="text-sm text-gray-300 mb-2 block">Mic Volume</label>
                  <Slider
                    value={micVolume}
                    onValueChange={setMicVolume}
                    max={100}
                    step={1}
                    className="w-full"
                  />
                  <div className="text-xs text-gray-400 mt-1">{micVolume[0]}%</div>
                </div>

                {selectedBeat && (
                  <div>
                    <label className="text-sm text-gray-300 mb-2 block">Beat Volume</label>
                    <Slider
                      value={beatVolume}
                      onValueChange={setBeatVolume}
                      max={100}
                      step={1}
                      className="w-full"
                    />
                    <div className="text-xs text-gray-400 mt-1">{beatVolume[0]}%</div>
                  </div>
                )}
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Beats panel */}
      <AnimatePresence>
        {showBeats && (
          <motion.div
            initial={{ y: '100%' }}
            animate={{ y: 0 }}
            exit={{ y: '100%' }}
            className="absolute bottom-0 left-0 right-0 h-80 bg-black/90 backdrop-blur-sm z-30 p-4"
          >
            <div className="text-white">
              <div className="flex items-center justify-between mb-4">
                <h3 className="font-semibold">Trending Beats</h3>
                <Button
                  variant="ghost"
                  onClick={() => setShowBeats(false)}
                  className="text-white"
                >
                  Ã—
                </Button>
              </div>

              <div className="grid grid-cols-1 gap-3 max-h-60 overflow-y-auto">
                {trendingBeats.map(beat => (
                  <div
                    key={beat.id}
                    className={`p-3 rounded-lg border cursor-pointer transition-all ${
                      selectedBeat?.id === beat.id 
                        ? 'border-purple-500 bg-purple-500/20' 
                        : 'border-gray-600 hover:border-gray-500'
                    }`}
                    onClick={() => selectBeat(beat)}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex-1">
                        <div className="flex items-center space-x-2">
                          <div className="font-semibold">{beat.name}</div>
                          {beat.trending && (
                            <Badge className="bg-red-500 text-xs">ðŸ”¥ Trending</Badge>
                          )}
                        </div>
                        <div className="text-sm text-gray-400">
                          by {beat.producer} â€¢ {beat.bpm} BPM â€¢ {beat.genre}
                        </div>
                      </div>
                      <div className="text-right">
                        {beat.price === 0 ? (
                          <Badge className="bg-green-500">Free</Badge>
                        ) : (
                          <div className="text-sm">${beat.price}</div>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Left side quick actions */}
      <div className="absolute left-4 top-1/2 transform -translate-y-1/2 z-30 space-y-4">
        <Button
          variant="ghost"
          size="icon"
          onClick={() => setShowBeats(true)}
          className="text-white bg-black/50 rounded-full"
        >
          <Music className="w-5 h-5" />
        </Button>

        <Button
          variant="ghost"
          size="icon"
          onClick={() => setShowEffects(true)}
          className="text-white bg-black/50 rounded-full"
        >
          <Sparkles className="w-5 h-5" />
        </Button>

        <Button
          variant="ghost"
          size="icon"
          onClick={() => setIsVideoEnabled(!isVideoEnabled)}
          className="text-white bg-black/50 rounded-full"
        >
          {isVideoEnabled ? <Camera className="w-5 h-5" /> : <CameraOff className="w-5 h-5" />}
        </Button>

        <Button
          variant="ghost"
          size="icon"
          onClick={() => setIsAudioEnabled(!isAudioEnabled)}
          className="text-white bg-black/50 rounded-full"
        >
          {isAudioEnabled ? <Mic className="w-5 h-5" /> : <MicOff className="w-5 h-5" />}
        </Button>
      </div>

      {/* Bottom recording controls */}
      <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 z-40">
        <div className="flex items-center space-x-6">
          {/* Timer */}
          <Button
            variant="ghost"
            onClick={startCountdown}
            className="text-white bg-black/50 rounded-full"
            disabled={isRecording}
          >
            <Timer className="w-5 h-5 mr-2" />
            3s
          </Button>

          {/* Main record button */}
          <div className="relative">
            <Button
              onMouseDown={isRecording ? undefined : startRecording}
              onMouseUp={isRecording ? undefined : stopRecording}
              onTouchStart={isRecording ? undefined : startRecording}
              onTouchEnd={isRecording ? undefined : stopRecording}
              onClick={isRecording ? (isPaused ? togglePause : stopRecording) : startCountdown}
              className={`w-20 h-20 rounded-full border-4 transition-all ${
                isRecording 
                  ? 'bg-red-500 border-red-300 scale-110' 
                  : 'bg-white border-gray-300 hover:scale-105'
              }`}
              disabled={countdown > 0}
            >
              {isRecording ? (
                isPaused ? <Play className="w-8 h-8 text-white" /> : <Square className="w-6 h-6 text-white" />
              ) : (
                <div className="w-6 h-6 bg-red-500 rounded-full" />
              )}
            </Button>

            {/* Recording ring */}
            {isRecording && (
              <div className="absolute inset-0 border-4 border-red-500 rounded-full animate-ping" />
            )}
          </div>

          {/* Upload existing */}
          <Button
            variant="ghost"
            className="text-white bg-black/50 rounded-full"
            disabled={isRecording}
          >
            <Upload className="w-5 h-5" />
          </Button>
        </div>
      </div>

      {/* Recording instructions */}
      {!isRecording && countdown === 0 && (
        <div className="absolute bottom-32 left-1/2 transform -translate-x-1/2 text-center text-white text-sm z-30">
          <div>Hold to record â€¢ Tap for 3s countdown</div>
          <div className="text-xs text-gray-400 mt-1">Up to 60 seconds</div>
        </div>
      )}
    </div>
  );
}

client/src/components/live-battle-system.content:
import { useState, useEffect, useRef, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import AudioStreamer from "@/components/audio-streamer";
import WebRTCManager from "@/components/webrtc-manager";
import { useAuth } from "@/hooks/useAuth";
import { useWebSocket } from "@/hooks/useWebSocket";
import { 
  Crown, Flame, Users, Timer, Mic, MicOff,
  Volume2, VolumeX, ThumbsUp, ThumbsDown,
  Star, Trophy, Medal, Zap, Target, Sword
} from "lucide-react";

interface Contestant {
  id: string;
  username: string;
  avatar: string;
  isVerified: boolean;
  votes: number;
  isCurrentTurn: boolean;
  streak: number;
  rating: number;
}

interface Battle {
  id: string;
  title: string;
  type: 'freestyle' | 'cypher' | 'tournament' | 'challenge';
  status: 'waiting' | 'active' | 'voting' | 'completed';
  contestant1: Contestant;
  contestant2: Contestant;
  timeLeft: number;
  totalTime: number;
  viewerCount: number;
  prize: number;
  round: number;
  maxRounds: number;
  beat?: {
    id: string;
    name: string;
    bpm: number;
  };
}

interface LiveBattleSystemProps {
  battle?: Battle;
  onVote?: (contestantId: string) => void;
  onJoinBattle?: () => void;
  onLeaveBattle?: () => void;
  isParticipating?: boolean;
  className?: string;
}

export default function LiveBattleSystem({
  battle,
  onVote,
  onJoinBattle,
  onLeaveBattle,
  isParticipating = false,
  className = ""
}: LiveBattleSystemProps) {
  const { user } = useAuth();
  const { joinRoom, leaveRoom, castVote } = useWebSocket();

  // Audio streaming state
  const [audioStream, setAudioStream] = useState<MediaStream | null>(null);
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [battleRoomId, setBattleRoomId] = useState<string>("");

  // Default battle data
  const defaultBattle: Battle = {
    id: 'live-battle-1',
    title: 'Epic Rap Battle: Fire vs Ice',
    type: 'freestyle',
    status: 'active',
    contestant1: {
      id: 'mc-fire',
      username: 'MC_Fire',
      avatar: '/api/placeholder/100/100?text=ðŸ”¥',
      isVerified: true,
      votes: 234,
      isCurrentTurn: true,
      streak: 5,
      rating: 1850
    },
    contestant2: {
      id: 'ice-cold',
      username: 'IceCold_Beats',
      avatar: '/api/placeholder/100/100?text=â„ï¸',
      isVerified: false,
      votes: 187,
      isCurrentTurn: false,
      streak: 3,
      rating: 1720
    },
    timeLeft: 45,
    totalTime: 60,
    viewerCount: 1247,
    prize: 500,
    round: 2,
    maxRounds: 3,
    beat: {
      id: 'battle-beat-1',
      name: 'Heavy Artillery',
      bpm: 140
    }
  };

  const currentBattle = battle || defaultBattle;

  const [hasVoted, setHasVoted] = useState(false);
  const [votedFor, setVotedFor] = useState<string | null>(null);
  const [timeRemaining, setTimeRemaining] = useState(currentBattle.timeLeft);
  const [showVotingResults, setShowVotingResults] = useState(false);
  const [battleEffects, setBattleEffects] = useState<{ id: string; type: string; x: number; y: number }[]>([]);

  const battleRef = useRef<HTMLDivElement>(null);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  // Battle timer
  useEffect(() => {
    if (currentBattle.status === 'active') {
      timerRef.current = setInterval(() => {
        setTimeRemaining(prev => {
          if (prev <= 1) {
            // Time's up, switch to voting
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [currentBattle.status]);

  // Handle voting
  const handleVote = useCallback((contestantId: string) => {
    if (hasVoted || currentBattle.status !== 'voting') return;

    setHasVoted(true);
    setVotedFor(contestantId);
    onVote?.(contestantId);

    // Send real-time vote via WebSocket
    if (user) {
      castVote(currentBattle.id, contestantId);
    }

    // Add visual effect
    const container = battleRef.current;
    if (container) {
      const rect = container.getBoundingClientRect();
      const effect = {
        id: `vote-${Date.now()}`,
        type: 'vote',
        x: Math.random() * rect.width,
        y: Math.random() * rect.height
      };
      setBattleEffects(prev => [...prev, effect]);

      setTimeout(() => {
        setBattleEffects(prev => prev.filter(e => e.id !== effect.id));
      }, 2000);
    }
  }, [hasVoted, currentBattle.status, onVote, user, castVote, currentBattle.id]);

  // Handle joining battle with audio streaming
  const handleJoinBattle = () => {
    if (user && currentBattle) {
      const roomId = `battle-${currentBattle.id}`;
      setBattleRoomId(roomId);
      joinRoom(roomId, user.displayName || user.username || 'Anonymous', false);
      onJoinBattle?.();
    }
  };

  // Handle leaving battle
  const handleLeaveBattle = () => {
    leaveRoom();
    setAudioStream(null);
    setBattleRoomId("");
    onLeaveBattle?.();
  };

  // Audio stream handlers
  const handleAudioStreamStart = (stream: MediaStream) => {
    setAudioStream(stream);
    setIsAudioEnabled(true);
  };

  const handleAudioStreamEnd = () => {
    setAudioStream(null);
    setIsAudioEnabled(false);
  };

  // Calculate vote percentages
  const totalVotes = currentBattle.contestant1.votes + currentBattle.contestant2.votes;
  const contestant1Percentage = totalVotes > 0 ? (currentBattle.contestant1.votes / totalVotes) * 100 : 50;
  const contestant2Percentage = totalVotes > 0 ? (currentBattle.contestant2.votes / totalVotes) * 100 : 50;

  // Format time
  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div 
      ref={battleRef}
      className={`relative w-full h-full bg-gradient-to-br from-red-900 via-purple-900 to-blue-900 ${className}`}
    >
      {/* Battle background effects */}
      <div className="absolute inset-0 bg-black/40" />
      <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent" />

      {/* Floating battle effects */}
      <AnimatePresence>
        {battleEffects.map(effect => (
          <motion.div
            key={effect.id}
            initial={{ scale: 0, x: effect.x, y: effect.y, opacity: 1 }}
            animate={{ 
              scale: [0, 1.5, 1],
              y: effect.y - 100,
              opacity: [1, 1, 0]
            }}
            exit={{ opacity: 0 }}
            transition={{ duration: 2 }}
            className="absolute pointer-events-none z-30"
          >
            {effect.type === 'vote' && (
              <div className="text-yellow-400">
                <Star className="w-8 h-8 fill-current" />
              </div>
            )}
          </motion.div>
        ))}
      </AnimatePresence>

      {/* Header */}
      <div className="absolute top-4 left-4 right-4 z-20">
        <div className="flex items-center justify-between text-white">
          <div className="flex items-center space-x-2">
            <Badge className="bg-red-500 text-white px-3 py-1 animate-pulse">
              ðŸ”´ LIVE BATTLE
            </Badge>
            <Badge variant="outline" className="text-white border-white">
              Round {currentBattle.round}/{currentBattle.maxRounds}
            </Badge>
          </div>

          <div className="flex items-center space-x-4 text-sm">
            <div className="flex items-center space-x-1">
              <Users className="w-4 h-4" />
              <span>{currentBattle.viewerCount.toLocaleString()}</span>
            </div>
            {currentBattle.prize > 0 && (
              <div className="flex items-center space-x-1">
                <Trophy className="w-4 h-4 text-yellow-400" />
                <span>${currentBattle.prize}</span>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Contestants */}
      <div className="absolute inset-0 flex items-center">
        {/* Contestant 1 */}
        <div className="flex-1 text-center text-white relative">
          <motion.div
            animate={currentBattle.contestant1.isCurrentTurn ? { scale: [1, 1.05, 1] } : {}}
            transition={{ duration: 1, repeat: Infinity }}
            className={`relative ${currentBattle.contestant1.isCurrentTurn ? 'ring-4 ring-blue-500 rounded-full' : ''}`}
          >
            <div className="text-6xl mb-4">{currentBattle.contestant1.avatar}</div>
            <div className="space-y-2">
              <div className="flex items-center justify-center space-x-2">
                <span className="text-xl font-bold">{currentBattle.contestant1.username}</span>
                {currentBattle.contestant1.isVerified && <Crown className="w-5 h-5 text-yellow-400" />}
              </div>

              <div className="flex items-center justify-center space-x-4 text-sm text-gray-300">
                <div className="flex items-center space-x-1">
                  <Flame className="w-4 h-4" />
                  <span>{currentBattle.contestant1.streak} streak</span>
                </div>
                <div className="flex items-center space-x-1">
                  <Star className="w-4 h-4" />
                  <span>{currentBattle.contestant1.rating}</span>
                </div>
              </div>

              {/* Vote count */}
              <div className="mt-4">
                <div className="text-3xl font-bold text-blue-400">{currentBattle.contestant1.votes}</div>
                <div className="text-sm text-gray-300">votes</div>
                <div className="mt-2 bg-gray-700 rounded-full h-2">
                  <motion.div 
                    className="bg-blue-500 h-2 rounded-full"
                    initial={{ width: 0 }}
                    animate={{ width: `${contestant1Percentage}%` }}
                    transition={{ duration: 0.5 }}
                  />
                </div>
              </div>
            </div>
          </motion.div>

          {/* Vote button */}
          {currentBattle.status === 'voting' && (
            <Button
              onClick={() => handleVote(currentBattle.contestant1.id)}
              disabled={hasVoted}
              className={`mt-4 ${
                votedFor === currentBattle.contestant1.id 
                  ? 'bg-blue-600 hover:bg-blue-700' 
                  : 'bg-blue-500 hover:bg-blue-600'
              } ${hasVoted && votedFor !== currentBattle.contestant1.id ? 'opacity-50' : ''}`}
              size="lg"
            >
              {votedFor === currentBattle.contestant1.id ? (
                <>
                  <Star className="w-5 h-5 mr-2 fill-current" />
                  Voted!
                </>
              ) : (
                <>
                  <ThumbsUp className="w-5 h-5 mr-2" />
                  Vote
                </>
              )}
            </Button>
          )}
        </div>

        {/* VS and Timer */}
        <div className="w-32 text-center text-white">
          <motion.div
            animate={{ scale: [1, 1.1, 1] }}
            transition={{ duration: 2, repeat: Infinity }}
            className="text-6xl font-bold mb-4 text-transparent bg-gradient-to-r from-red-500 to-blue-500 bg-clip-text"
          >
            VS
          </motion.div>

          {/* Timer */}
          <div className="space-y-2">
            <div className="flex items-center justify-center space-x-1">
              <Timer className="w-5 h-5" />
              <span className="text-xl font-mono">{formatTime(timeRemaining)}</span>
            </div>

            <Progress 
              value={(timeRemaining / currentBattle.totalTime) * 100}
              className="w-20 h-2 mx-auto"
            />

            <div className="text-xs text-gray-300">
              {currentBattle.status === 'active' ? 'Battle Time' : 
               currentBattle.status === 'voting' ? 'Voting Time' : 
               currentBattle.status === 'waiting' ? 'Starting Soon' : 'Complete'}
            </div>
          </div>

          {/* Beat info */}
          {currentBattle.beat && (
            <div className="mt-4 text-xs text-gray-300">
              <div className="flex items-center justify-center space-x-1">
                <Volume2 className="w-3 h-3" />
                <span>{currentBattle.beat.name}</span>
              </div>
              <div>{currentBattle.beat.bpm} BPM</div>
            </div>
          )}
        </div>

        {/* Contestant 2 */}
        <div className="flex-1 text-center text-white relative">
          <motion.div
            animate={currentBattle.contestant2.isCurrentTurn ? { scale: [1, 1.05, 1] } : {}}
            transition={{ duration: 1, repeat: Infinity }}
            className={`relative ${currentBattle.contestant2.isCurrentTurn ? 'ring-4 ring-red-500 rounded-full' : ''}`}
          >
            <div className="text-6xl mb-4">{currentBattle.contestant2.avatar}</div>
            <div className="space-y-2">
              <div className="flex items-center justify-center space-x-2">
                <span className="text-xl font-bold">{currentBattle.contestant2.username}</span>
                {currentBattle.contestant2.isVerified && <Crown className="w-5 h-5 text-yellow-400" />}
              </div>

              <div className="flex items-center justify-center space-x-4 text-sm text-gray-300">
                <div className="flex items-center space-x-1">
                  <Flame className="w-4 h-4" />
                  <span>{currentBattle.contestant2.streak} streak</span>
                </div>
                <div className="flex items-center space-x-1">
                  <Star className="w-4 h-4" />
                  <span>{currentBattle.contestant2.rating}</span>
                </div>
              </div>

              {/* Vote count */}
              <div className="mt-4">
                <div className="text-3xl font-bold text-red-400">{currentBattle.contestant2.votes}</div>
                <div className="text-sm text-gray-300">votes</div>
                <div className="mt-2 bg-gray-700 rounded-full h-2">
                  <motion.div 
                    className="bg-red-500 h-2 rounded-full"
                    initial={{ width: 0 }}
                    animate={{ width: `${contestant2Percentage}%` }}
                    transition={{ duration: 0.5 }}
                  />
                </div>
              </div>
            </div>
          </motion.div>

          {/* Vote button */}
          {currentBattle.status === 'voting' && (
            <Button
              onClick={() => handleVote(currentBattle.contestant2.id)}
              disabled={hasVoted}
              className={`mt-4 ${
                votedFor === currentBattle.contestant2.id 
                  ? 'bg-red-600 hover:bg-red-700' 
                  : 'bg-red-500 hover:bg-red-600'
              } ${hasVoted && votedFor !== currentBattle.contestant2.id ? 'opacity-50' : ''}`}
              size="lg"
            >
              {votedFor === currentBattle.contestant2.id ? (
                <>
                  <Star className="w-5 h-5 mr-2 fill-current" />
                  Voted!
                </>
              ) : (
                <>
                  <ThumbsUp className="w-5 h-5 mr-2" />
                  Vote
                </>
              )}
            </Button>
          )}
        </div>
      </div>

      {/* Audio streaming for participants */}
      {isParticipating && battleRoomId && (
        <div className="absolute bottom-20 left-4 right-4 z-20">
          <AudioStreamer
            isStreaming={true}
            onStreamStart={handleAudioStreamStart}
            onStreamEnd={handleAudioStreamEnd}
            quality="medium"
            roomId={battleRoomId}
          />

          {/* WebRTC for peer connections */}
          {user && (
            <WebRTCManager
              roomId={battleRoomId}
              username={user.displayName || user.username || 'Anonymous'}
              isHost={false}
              enableAudio={isAudioEnabled}
              enableVideo={false}
              maxPeers={2}
            />
          )}
        </div>
      )}

      {/* Bottom actions */}
      <div className="absolute bottom-4 left-4 right-4 z-20">
        <div className="flex items-center justify-between">
          {!isParticipating ? (
            <Button
              onClick={handleJoinBattle}
              className="bg-green-500 hover:bg-green-600 text-white"
              size="lg"
            >
              <Mic className="w-5 h-5 mr-2" />
              Join Battle
            </Button>
          ) : (
            <Button
              onClick={handleLeaveBattle}
              variant="outline"
              className="border-red-500 text-red-500 hover:bg-red-500 hover:text-white"
              size="lg"
            >
              <MicOff className="w-5 h-5 mr-2" />
              Leave
            </Button>
          )}

          <div className="flex space-x-2">
            <Button 
              variant="outline" 
              size="icon" 
              className={`text-white border-white ${isAudioEnabled ? 'bg-green-500' : 'bg-red-500'}`}
              onClick={() => setIsAudioEnabled(!isAudioEnabled)}
              disabled={!isParticipating}
            >
              {isAudioEnabled ? <Volume2 className="w-5 h-5" /> : <VolumeX className="w-5 h-5" />}
            </Button>
            <Button variant="outline" size="icon" className="text-white border-white">
              <Target className="w-5 h-5" />
            </Button>
          </div>
        </div>
      </div>

      {/* Status overlay */}
      <AnimatePresence>
        {currentBattle.status === 'waiting' && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="absolute inset-0 bg-black/80 flex items-center justify-center z-30"
          >
            <div className="text-center text-white">
              <Sword className="w-16 h-16 mx-auto mb-4 text-yellow-400" />
              <div className="text-2xl font-bold mb-2">Battle Starting Soon</div>
              <div className="text-gray-300">Waiting for contestants...</div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

