            </Card>

            {/* Payout Information */}
            <Card className="bg-black/50 border-gray-700 p-4">
              <h3 className="font-semibold mb-4">Payout Information</h3>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <div className="text-lg font-bold text-green-400">$2,847.32</div>
                  <div className="text-sm text-gray-400">Available for Payout</div>
                </div>
                <div>
                  <div className="text-lg font-bold">$1,245.67</div>
                  <div className="text-sm text-gray-400">Pending Clearance</div>
                </div>
                <div>
                  <div className="text-lg font-bold">Next: March 15</div>
                  <div className="text-sm text-gray-400">Payout Date</div>
                </div>
              </div>

              <Button className="w-full mt-4 bg-green-500 hover:bg-green-600">
                Request Payout
              </Button>
            </Card>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/quality-controls.t.content:
import { useState, useCallback } from "react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Switch } from "@/components/ui/switch";
import { 
  Settings, Monitor, Wifi, Zap, Activity, 
  HardDrive, Clock, Gauge, Volume2 
} from "lucide-react";

interface QualitySettings {
  video: {
    resolution: string;
    frameRate: number;
    bitrate: number;
    codec: string;
  };
  audio: {
    sampleRate: number;
    bitrate: number;
    channels: number;
    codec: string;
  };
  streaming: {
    adaptiveBitrate: boolean;
    lowLatencyMode: boolean;
    bufferSize: number;
    keyFrameInterval: number;
  };
  performance: {
    hardwareAcceleration: boolean;
    multiThreading: boolean;
    cpuUsageLimit: number;
    memoryUsageLimit: number;
  };
}

interface QualityControlsProps {
  settings?: Partial<QualitySettings>;
  onSettingsChange?: (settings: QualitySettings) => void;
  presets?: Array<{ name: string; settings: QualitySettings }>;
  showAdvanced?: boolean;
  currentBitrate?: number;
  currentFPS?: number;
  cpuUsage?: number;
  memoryUsage?: number;
  networkLatency?: number;
}

export default function QualityControls({
  settings,
  onSettingsChange,
  presets = [],
  showAdvanced = false,
  currentBitrate = 0,
  currentFPS = 0,
  cpuUsage = 0,
  memoryUsage = 0,
  networkLatency = 0
}: QualityControlsProps) {
  const [currentSettings, setCurrentSettings] = useState<QualitySettings>({
    video: {
      resolution: '1280x720',
      frameRate: 30,
      bitrate: 2500,
      codec: 'h264'
    },
    audio: {
      sampleRate: 44100,
      bitrate: 128,
      channels: 2,
      codec: 'aac'
    },
    streaming: {
      adaptiveBitrate: true,
      lowLatencyMode: true,
      bufferSize: 1024,
      keyFrameInterval: 2
    },
    performance: {
      hardwareAcceleration: true,
      multiThreading: true,
      cpuUsageLimit: 80,
      memoryUsageLimit: 75
    },
    ...settings
  });

  const [selectedPreset, setSelectedPreset] = useState<string>('custom');
  const [showPerformanceMetrics, setShowPerformanceMetrics] = useState(true);

  // Quality presets
  // Mobile-first presets with better data usage
  const defaultPresets = [
    {
      name: 'Mobile Data Saver',
      settings: {
        video: { resolution: '480x360', frameRate: 15, bitrate: 300, codec: 'h264' },
        audio: { sampleRate: 22050, bitrate: 32, channels: 1, codec: 'aac' },
        streaming: { adaptiveBitrate: true, lowLatencyMode: true, bufferSize: 128, keyFrameInterval: 4 },
        performance: { hardwareAcceleration: false, multiThreading: false, cpuUsageLimit: 40, memoryUsageLimit: 30 }
      }
    },
    {
      name: 'Mobile Standard',
      settings: {
        video: { resolution: '640x480', frameRate: 20, bitrate: 600, codec: 'h264' },
        audio: { sampleRate: 22050, bitrate: 64, channels: 1, codec: 'aac' },
        streaming: { adaptiveBitrate: true, lowLatencyMode: true, bufferSize: 256, keyFrameInterval: 3 },
        performance: { hardwareAcceleration: true, multiThreading: false, cpuUsageLimit: 50, memoryUsageLimit: 40 }
      }
    },
    {
      name: 'Mobile WiFi',
      settings: {
        video: { resolution: '854x480', frameRate: 24, bitrate: 1000, codec: 'h264' },
        audio: { sampleRate: 44100, bitrate: 96, channels: 2, codec: 'aac' },
        streaming: { adaptiveBitrate: true, lowLatencyMode: true, bufferSize: 512, keyFrameInterval: 2 },
        performance: { hardwareAcceleration: true, multiThreading: true, cpuUsageLimit: 60, memoryUsageLimit: 50 }
      }
    },
    {
      name: 'Desktop High',
      settings: {
        video: { resolution: '1280x720', frameRate: 30, bitrate: 2500, codec: 'h264' },
        audio: { sampleRate: 44100, bitrate: 128, channels: 2, codec: 'aac' },
        streaming: { adaptiveBitrate: true, lowLatencyMode: true, bufferSize: 1024, keyFrameInterval: 2 },
        performance: { hardwareAcceleration: true, multiThreading: true, cpuUsageLimit: 70, memoryUsageLimit: 65 }
      }
    },
    {
      name: 'Desktop Ultra',
      settings: {
        video: { resolution: '1920x1080', frameRate: 60, bitrate: 5000, codec: 'h264' },
        audio: { sampleRate: 48000, bitrate: 192, channels: 2, codec: 'aac' },
        streaming: { adaptiveBitrate: true, lowLatencyMode: false, bufferSize: 2048, keyFrameInterval: 2 },
        performance: { hardwareAcceleration: true, multiThreading: true, cpuUsageLimit: 85, memoryUsageLimit: 80 }
      }
    }
  ];

  const allPresets = [...defaultPresets, ...presets];

  // Update settings
  const updateSettings = useCallback((newSettings: Partial<QualitySettings>) => {
    const updated = {
      ...currentSettings,
      ...newSettings,
      video: { ...currentSettings.video, ...newSettings.video },
      audio: { ...currentSettings.audio, ...newSettings.audio },
      streaming: { ...currentSettings.streaming, ...newSettings.streaming },
      performance: { ...currentSettings.performance, ...newSettings.performance }
    };

    setCurrentSettings(updated);
    onSettingsChange?.(updated);
  }, [currentSettings, onSettingsChange]);

  // Apply preset
  const applyPreset = useCallback((presetName: string) => {
    const preset = allPresets.find(p => p.name === presetName);
    if (preset) {
      setCurrentSettings(preset.settings);
      onSettingsChange?.(preset.settings);
    }
    setSelectedPreset(presetName);
  }, [allPresets, onSettingsChange]);

  // Get quality status
  const getQualityStatus = () => {
    const totalBitrate = currentSettings.video.bitrate + currentSettings.audio.bitrate;

    if (totalBitrate >= 6000) return { label: 'Ultra', color: 'bg-purple-500' };
    if (totalBitrate >= 3000) return { label: 'High', color: 'bg-green-500' };
    if (totalBitrate >= 1500) return { label: 'Medium', color: 'bg-yellow-500' };
    return { label: 'Low', color: 'bg-red-500' };
  };

  const qualityStatus = getQualityStatus();

  return (
    <Card className="bg-dark-200 border-dark-400 p-6">
      <div className="space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Settings className="w-5 h-5 text-purple-500" />
            <h3 className="text-lg font-semibold text-white">Quality Controls</h3>
          </div>

          <div className="flex items-center space-x-2">
            <Badge className={`${qualityStatus.color} text-white`}>
              {qualityStatus.label} Quality
            </Badge>
            {showPerformanceMetrics && (
              <Button
                onClick={() => setShowPerformanceMetrics(!showPerformanceMetrics)}
                variant="outline"
                size="sm"
                className="border-dark-400 text-white"
                data-testid="toggle-metrics"
              >
                <Activity className="w-4 h-4" />
              </Button>
            )}
          </div>
        </div>

        {/* Performance Metrics */}
        {showPerformanceMetrics && (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="space-y-1">
              <div className="flex items-center space-x-1">
                <Gauge className="w-3 h-3 text-gray-400" />
                <span className="text-xs text-gray-400">Bitrate</span>
              </div>
              <div className="text-sm font-medium text-white">{currentBitrate}kbps</div>
            </div>

            <div className="space-y-1">
              <div className="flex items-center space-x-1">
                <Monitor className="w-3 h-3 text-gray-400" />
                <span className="text-xs text-gray-400">FPS</span>
              </div>
              <div className="text-sm font-medium text-white">{currentFPS}</div>
            </div>

            <div className="space-y-1">
              <div className="flex items-center space-x-1">
                <Zap className="w-3 h-3 text-gray-400" />
                <span className="text-xs text-gray-400">CPU</span>
              </div>
              <div className={`text-sm font-medium ${
                cpuUsage > 80 ? 'text-red-500' : cpuUsage > 60 ? 'text-yellow-500' : 'text-green-500'
              }`}>
                {cpuUsage}%
              </div>
            </div>

            <div className="space-y-1">
              <div className="flex items-center space-x-1">
                <Clock className="w-3 h-3 text-gray-400" />
                <span className="text-xs text-gray-400">Latency</span>
              </div>
              <div className={`text-sm font-medium ${
                networkLatency > 100 ? 'text-red-500' : networkLatency > 50 ? 'text-yellow-500' : 'text-green-500'
              }`}>
                {networkLatency}ms
              </div>
            </div>
          </div>
        )}

        {/* Preset Selection */}
        <div className="space-y-2">
          <label className="text-sm text-gray-400">Quality Preset</label>
          <Select value={selectedPreset} onValueChange={applyPreset}>
            <SelectTrigger className="bg-dark-300 border-dark-400 text-white">
              <SelectValue />
            </SelectTrigger>
            <SelectContent className="bg-dark-300 border-dark-400">
              <SelectItem value="custom">Custom</SelectItem>
              {allPresets.map((preset) => (
                <SelectItem key={preset.name} value={preset.name}>
                  {preset.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        {/* Video Settings */}
        <div className="space-y-4">
          <h4 className="text-sm font-semibold text-white flex items-center">
            <Monitor className="w-4 h-4 mr-2" />
            Video Settings
          </h4>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <label className="text-xs text-gray-400">Resolution</label>
              <Select 
                value={currentSettings.video.resolution} 
                onValueChange={(value) => updateSettings({ video: { ...currentSettings.video, resolution: value } })}
              >
                <SelectTrigger className="bg-dark-300 border-dark-400 text-white text-xs">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-dark-300 border-dark-400">
                  <SelectItem value="3840x2160">4K (3840x2160)</SelectItem>
                  <SelectItem value="1920x1080">1080p (1920x1080)</SelectItem>
                  <SelectItem value="1280x720">720p (1280x720)</SelectItem>
                  <SelectItem value="854x480">480p (854x480)</SelectItem>
                  <SelectItem value="640x360">360p (640x360)</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <label className="text-xs text-gray-400">Frame Rate</label>
              <Select 
                value={currentSettings.video.frameRate.toString()} 
                onValueChange={(value) => updateSettings({ video: { ...currentSettings.video, frameRate: parseInt(value) } })}
              >
                <SelectTrigger className="bg-dark-300 border-dark-400 text-white text-xs">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-dark-300 border-dark-400">
                  <SelectItem value="15">15 FPS</SelectItem>
                  <SelectItem value="24">24 FPS</SelectItem>
                  <SelectItem value="30">30 FPS</SelectItem>
                  <SelectItem value="60">60 FPS</SelectItem>
                  <SelectItem value="120">120 FPS</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <label className="text-xs text-gray-400">Video Bitrate</label>
              <span className="text-xs text-white">{currentSettings.video.bitrate} kbps</span>
            </div>
            <Slider
              value={[currentSettings.video.bitrate]}
              onValueChange={(value) => updateSettings({ video: { ...currentSettings.video, bitrate: value[0] } })}
              min={100}
              max={10000}
              step={100}
              className="w-full"
              data-testid="video-bitrate-slider"
            />
          </div>
        </div>

        {/* Audio Settings */}
        <div className="space-y-4">
          <h4 className="text-sm font-semibold text-white flex items-center">
            <Volume2 className="w-4 h-4 mr-2" />
            Audio Settings
          </h4>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <label className="text-xs text-gray-400">Sample Rate</label>
              <Select 
                value={currentSettings.audio.sampleRate.toString()} 
                onValueChange={(value) => updateSettings({ audio: { ...currentSettings.audio, sampleRate: parseInt(value) } })}
              >
                <SelectTrigger className="bg-dark-300 border-dark-400 text-white text-xs">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-dark-300 border-dark-400">
                  <SelectItem value="22050">22.05 kHz</SelectItem>
                  <SelectItem value="44100">44.1 kHz</SelectItem>
                  <SelectItem value="48000">48 kHz</SelectItem>
                  <SelectItem value="96000">96 kHz</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="space-y-2">
              <label className="text-xs text-gray-400">Channels</label>
              <Select 
                value={currentSettings.audio.channels.toString()} 
                onValueChange={(value) => updateSettings({ audio: { ...currentSettings.audio, channels: parseInt(value) } })}
              >
                <SelectTrigger className="bg-dark-300 border-dark-400 text-white text-xs">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent className="bg-dark-300 border-dark-400">
                  <SelectItem value="1">Mono</SelectItem>
                  <SelectItem value="2">Stereo</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <label className="text-xs text-gray-400">Audio Bitrate</label>
              <span className="text-xs text-white">{currentSettings.audio.bitrate} kbps</span>
            </div>
            <Slider
              value={[currentSettings.audio.bitrate]}
              onValueChange={(value) => updateSettings({ audio: { ...currentSettings.audio, bitrate: value[0] } })}
              min={32}
              max={320}
              step={8}
              className="w-full"
              data-testid="audio-bitrate-slider"
            />
          </div>
        </div>

        {/* Streaming Settings */}
        <div className="space-y-4">
          <h4 className="text-sm font-semibold text-white flex items-center">
            <Wifi className="w-4 h-4 mr-2" />
            Streaming Settings
          </h4>

          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <div>
                <span className="text-sm text-white">Adaptive Bitrate</span>
                <p className="text-xs text-gray-400">Automatically adjust quality based on connection</p>
              </div>
              <Switch
                checked={currentSettings.streaming.adaptiveBitrate}
                onCheckedChange={(checked) => updateSettings({ streaming: { ...currentSettings.streaming, adaptiveBitrate: checked } })}
                data-testid="adaptive-bitrate-switch"
              />
            </div>

            <div className="flex items-center justify-between">
              <div>
                <span className="text-sm text-white">Low Latency Mode</span>
                <p className="text-xs text-gray-400">Reduce delay for real-time interaction</p>
              </div>
              <Switch
                checked={currentSettings.streaming.lowLatencyMode}
                onCheckedChange={(checked) => updateSettings({ streaming: { ...currentSettings.streaming, lowLatencyMode: checked } })}
                data-testid="low-latency-switch"
              />
            </div>
          </div>

          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <label className="text-xs text-gray-400">Buffer Size</label>
              <span className="text-xs text-white">{currentSettings.streaming.bufferSize} KB</span>
            </div>
            <Slider
              value={[currentSettings.streaming.bufferSize]}
              onValueChange={(value) => updateSettings({ streaming: { ...currentSettings.streaming, bufferSize: value[0] } })}
              min={256}
              max={4096}
              step={256}
              className="w-full"
              data-testid="buffer-size-slider"
            />
          </div>
        </div>

        {/* Advanced Settings */}
        {showAdvanced && (
          <div className="space-y-4">
            <h4 className="text-sm font-semibold text-white flex items-center">
              <HardDrive className="w-4 h-4 mr-2" />
              Performance Settings
            </h4>

            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <div>
                  <span className="text-sm text-white">Hardware Acceleration</span>
                  <p className="text-xs text-gray-400">Use GPU for encoding when available</p>
                </div>
                <Switch
                  checked={currentSettings.performance.hardwareAcceleration}
                  onCheckedChange={(checked) => updateSettings({ performance: { ...currentSettings.performance, hardwareAcceleration: checked } })}
                  data-testid="hardware-acceleration-switch"
                />
              </div>

              <div className="flex items-center justify-between">
                <div>
                  <span className="text-sm text-white">Multi-threading</span>
                  <p className="text-xs text-gray-400">Use multiple CPU cores for processing</p>
                </div>
                <Switch
                  checked={currentSettings.performance.multiThreading}
                  onCheckedChange={(checked) => updateSettings({ performance: { ...currentSettings.performance, multiThreading: checked } })}
                  data-testid="multi-threading-switch"
                />
              </div>
            </div>

            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <label className="text-xs text-gray-400">CPU Usage Limit</label>
                <span className="text-xs text-white">{currentSettings.performance.cpuUsageLimit}%</span>
              </div>
              <Slider
                value={[currentSettings.performance.cpuUsageLimit]}
                onValueChange={(value) => updateSettings({ performance: { ...currentSettings.performance, cpuUsageLimit: value[0] } })}
                min={30}
                max={100}
                step={5}
                className="w-full"
                data-testid="cpu-limit-slider"
              />
            </div>
          </div>
        )}
      </div>
    </Card>
  );
}

client/src/components/social-interaction.content:
import { useState, useEffect, useRef, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Heart, MessageCircle, Share, Gift, Star,
  Flame, Crown, Zap, Music, ThumbsUp,
  Smile, Angry
} from "lucide-react";

interface FloatingReaction {
  id: string;
  type: 'heart' | 'fire' | 'crown' | 'zap' | 'music';
  x: number;
  y: number;
  color: string;
  size: number;
}

interface FloatingComment {
  id: string;
  text: string;
  user: string;
  avatar: string;
  timestamp: number;
  x: number;
  y: number;
}

interface Gift {
  id: string;
  name: string;
  icon: string;
  value: number;
  animation: string;
}

interface SocialInteractionsProps {
  streamId: string;
  onLike?: () => void;
  onComment?: (comment: string) => void;
  onShare?: () => void;
  onGift?: (giftId: string) => void;
  className?: string;
}

export default function SocialInteractions({
  streamId,
  onLike,
  onComment,
  onShare,
  onGift,
  className = ""
}: SocialInteractionsProps) {
  const [reactions, setReactions] = useState<FloatingReaction[]>([]);
  const [comments, setComments] = useState<FloatingComment[]>([]);
  const [showGifts, setShowGifts] = useState(false);
  const [showEmojis, setShowEmojis] = useState(false);
  const [isLiked, setIsLiked] = useState(false);
  const [likeCount, setLikeCount] = useState(0);
  const [commentText, setCommentText] = useState('');

  const containerRef = useRef<HTMLDivElement>(null);

  // Gift options
  const gifts: Gift[] = [
    { id: 'rose', name: 'Rose', icon: 'ðŸŒ¹', value: 1, animation: 'pulse' },
    { id: 'star', name: 'Star', icon: 'â­', value: 5, animation: 'spin' },
    { id: 'fire', name: 'Fire', icon: 'ðŸ”¥', value: 10, animation: 'bounce' },
    { id: 'crown', name: 'Crown', icon: 'ðŸ‘‘', value: 25, animation: 'glow' },
    { id: 'diamond', name: 'Diamond', icon: 'ðŸ’Ž', value: 50, animation: 'sparkle' },
    { id: 'rocket', name: 'Rocket', icon: 'ðŸš€', value: 100, animation: 'rocket' }
  ];

  // Emoji reactions
  const emojiReactions = [
    { id: 'heart', icon: 'â¤ï¸', color: 'text-red-500' },
    { id: 'fire', icon: 'ðŸ”¥', color: 'text-orange-500' },
    { id: 'laugh', icon: 'ðŸ˜‚', color: 'text-yellow-500' },
    { id: 'wow', icon: 'ðŸ˜®', color: 'text-blue-500' },
    { id: 'crown', icon: 'ðŸ‘‘', color: 'text-yellow-400' },
    { id: 'clap', icon: 'ðŸ‘', color: 'text-green-500' }
  ];

  // Add floating reaction
  const addReaction = useCallback((type: FloatingReaction['type'], event?: React.MouseEvent) => {
    const container = containerRef.current;
    if (!container) return;

    const rect = container.getBoundingClientRect();
    const x = event ? event.clientX - rect.left : Math.random() * rect.width;
    const y = event ? event.clientY - rect.top : rect.height - 100;

    const colors = {
      heart: 'text-red-500',
      fire: 'text-orange-500',
      crown: 'text-yellow-400',
      zap: 'text-blue-500',
      music: 'text-purple-500'
    };

    const reactionId = `reaction-${Date.now()}-${Math.random()}`;
    const newReaction: FloatingReaction = {
      id: reactionId,
      type,
      x,
      y,
      color: colors[type],
      size: 24 + Math.random() * 16
    };

    setReactions(prev => [...prev, newReaction]);

    // Remove after animation
    setTimeout(() => {
      setReactions(prev => prev.filter(r => r.id !== reactionId));
    }, 3000);
  }, []);

  // Add floating comment
  const addFloatingComment = useCallback((text: string) => {
    const container = containerRef.current;
    if (!container) return;

    const rect = container.getBoundingClientRect();
    const commentId = `comment-${Date.now()}-${Math.random()}`;

    const newComment: FloatingComment = {
      id: commentId,
      text,
      user: 'You', // Would come from user context
      avatar: 'ðŸ‘¤',
      timestamp: Date.now(),
      x: Math.random() * (rect.width - 200),
      y: rect.height - 150
    };

    setComments(prev => [...prev, newComment]);

    // Remove after animation
    setTimeout(() => {
      setComments(prev => prev.filter(c => c.id !== commentId));
    }, 5000);
  }, []);

  // Handle like
  const handleLike = useCallback((event?: React.MouseEvent) => {
    setIsLiked(!isLiked);
    setLikeCount(prev => isLiked ? prev - 1 : prev + 1);
    addReaction('heart', event);
    onLike?.();
  }, [isLiked, addReaction, onLike]);

  // Handle comment submission
  const handleCommentSubmit = useCallback(() => {
    if (commentText.trim()) {
      addFloatingComment(commentText);
      onComment?.(commentText);
      setCommentText('');
    }
  }, [commentText, addFloatingComment, onComment]);

  // Handle gift sending
  const handleGiftSend = useCallback((gift: Gift) => {
    // Create special gift animation
    const container = containerRef.current;
    if (!container) return;

    // Add gift reaction
    addReaction('crown');
    onGift?.(gift.id);
    setShowGifts(false);

    // Show gift notification
    console.log(`Sent ${gift.name} worth ${gift.value} coins`);
  }, [addReaction, onGift]);

  // Render reaction icon
  const renderReactionIcon = (type: FloatingReaction['type']) => {
    const iconMap = {
      heart: Heart,
      fire: Flame,
      crown: Crown,
      zap: Zap,
      music: Music
    };

    const IconComponent = iconMap[type];
    return <IconComponent className="w-full h-full" />;
  };

  // Auto-generate reactions
  useEffect(() => {
    const interval = setInterval(() => {
      if (Math.random() > 0.7) {
        const types: FloatingReaction['type'][] = ['heart', 'fire', 'crown', 'zap', 'music'];
        const randomType = types[Math.floor(Math.random() * types.length)];
        addReaction(randomType);
      }
    }, 2000);

    return () => clearInterval(interval);
  }, [addReaction]);

  return (
    <div 
      ref={containerRef}
      className={`relative h-full w-full ${className}`}
    >
      {/* Floating Reactions */}
      <AnimatePresence>
        {reactions.map(reaction => (
          <motion.div
            key={reaction.id}
            initial={{ 
              scale: 0,
              x: reaction.x,
              y: reaction.y,
              opacity: 1
            }}
            animate={{ 
              scale: [0, 1.2, 1],
              y: reaction.y - 200,
              x: reaction.x + (Math.random() - 0.5) * 100,
              opacity: [1, 1, 0],
              rotate: (Math.random() - 0.5) * 360
            }}
            exit={{ opacity: 0, scale: 0 }}
            transition={{ 
              duration: 3,
              ease: "easeOut"
            }}
            className={`absolute pointer-events-none z-50 ${reaction.color}`}
            style={{ 
              width: reaction.size, 
              height: reaction.size 
            }}
          >
            {renderReactionIcon(reaction.type)}
          </motion.div>
        ))}
      </AnimatePresence>

      {/* Floating Comments */}
      <AnimatePresence>
        {comments.map(comment => (
          <motion.div
            key={comment.id}
            initial={{ 
              x: comment.x,
              y: comment.y,
              opacity: 0,
              scale: 0.8
            }}
            animate={{ 
              y: comment.y - 100,
              opacity: [0, 1, 1, 0],
              scale: 1
            }}
            exit={{ opacity: 0 }}
            transition={{ 
              duration: 5,
              ease: "easeOut"
            }}
            className="absolute z-40 pointer-events-none"
          >
            <div className="bg-black/80 text-white px-3 py-2 rounded-full text-sm max-w-48 backdrop-blur-sm">
              <span className="font-semibold text-purple-400">{comment.user}: </span>
              <span>{comment.text}</span>
            </div>
          </motion.div>
        ))}
      </AnimatePresence>

      {/* Action Buttons Overlay */}
      <div className="absolute bottom-20 right-4 z-30 space-y-4">
        {/* Like Button */}
        <div className="text-center">
          <Button
            variant="ghost"
            size="icon"
            className={`w-12 h-12 rounded-full backdrop-blur-sm transition-all ${
              isLiked 
                ? 'bg-red-500/80 text-white scale-110' 
                : 'bg-black/50 text-white hover:bg-red-500/30'
            }`}
            onClick={handleLike}
          >
            <Heart className={`w-6 h-6 ${isLiked ? 'fill-current' : ''}`} />
          </Button>
          <div className="text-white text-xs mt-1">{likeCount}</div>
        </div>

        {/* Comment Button */}
        <div className="text-center">
          <Button
            variant="ghost"
            size="icon"
            className="w-12 h-12 rounded-full bg-black/50 text-white backdrop-blur-sm hover:bg-blue-500/30"
            onClick={() => setShowEmojis(!showEmojis)}
          >
            <MessageCircle className="w-6 h-6" />
          </Button>
          <div className="text-white text-xs mt-1">Chat</div>
        </div>

        {/* Gift Button */}
        <div className="text-center">
          <Button
            variant="ghost"
            size="icon"
            className="w-12 h-12 rounded-full bg-black/50 text-white backdrop-blur-sm hover:bg-yellow-500/30"
            onClick={() => setShowGifts(!showGifts)}
          >
            <Gift className="w-6 h-6" />
          </Button>
          <div className="text-white text-xs mt-1">Gift</div>
        </div>

        {/* Share Button */}
        <div className="text-center">
          <Button
            variant="ghost"
            size="icon"
            className="w-12 h-12 rounded-full bg-black/50 text-white backdrop-blur-sm hover:bg-green-500/30"
            onClick={onShare}
          >
            <Share className="w-6 h-6" />
          </Button>
          <div className="text-white text-xs mt-1">Share</div>
        </div>
      </div>

      {/* Quick Emoji Reactions */}
      <AnimatePresence>
        {showEmojis && (
          <motion.div
            initial={{ opacity: 0, x: 100 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: 100 }}
            className="absolute bottom-20 right-20 z-40"
          >
            <div className="flex flex-col space-y-2">
              {emojiReactions.map(emoji => (
                <Button
                  key={emoji.id}
                  variant="ghost"
                  size="icon"
                  className="w-10 h-10 rounded-full bg-black/70 backdrop-blur-sm hover:scale-110 transition-transform"
                  onClick={(e) => {
                    addReaction(emoji.id as FloatingReaction['type'], e);
                    setShowEmojis(false);
                  }}
                >
                  <span className="text-lg">{emoji.icon}</span>
                </Button>
              ))}
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Gift Panel */}
      <AnimatePresence>
        {showGifts && (
          <motion.div
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 100 }}
            className="absolute bottom-0 left-0 right-0 z-40 bg-black/90 backdrop-blur-sm p-4"
          >
            <div className="text-white">
              <div className="flex items-center justify-between mb-4">
                <h3 className="font-semibold">Send a Gift</h3>
                <Button
                  variant="ghost"
                  onClick={() => setShowGifts(false)}
                  className="text-white"
                >
                  Ã—
                </Button>
              </div>

              <div className="grid grid-cols-3 gap-3">
                {gifts.map(gift => (
                  <Button
                    key={gift.id}
                    variant="outline"
                    className="flex flex-col items-center p-4 h-auto border-gray-600 hover:border-yellow-500 hover:bg-yellow-500/10"
                    onClick={() => handleGiftSend(gift)}
                  >
                    <span className="text-2xl mb-2">{gift.icon}</span>
                    <span className="text-sm font-medium">{gift.name}</span>
                    <span className="text-xs text-yellow-400">{gift.value} coins</span>
                  </Button>
                ))}
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Comment Input */}
      <div className="absolute bottom-4 left-4 right-20 z-30">
        <div className="flex space-x-2">
          <input
            type="text"
            value={commentText}
            onChange={(e) => setCommentText(e.target.value)}
            placeholder="Add a comment..."
            className="flex-1 bg-black/70 text-white px-4 py-2 rounded-full backdrop-blur-sm border border-gray-600 focus:border-purple-500 focus:outline-none"
            onKeyPress={(e) => e.key === 'Enter' && handleCommentSubmit()}
            maxLength={100}
          />
          <Button
            onClick={handleCommentSubmit}
            className="bg-purple-500 hover:bg-purple-600 rounded-full px-6"
            disabled={!commentText.trim()}
          >
            Send
          </Button>
        </div>
      </div>

      {/* Live Activity Indicator */}
      <div className="absolute top-4 right-4 z-30">
        <div className="flex items-center space-x-2 text-white text-sm bg-black/70 px-3 py-1 rounded-full backdrop-blur-sm">
          <div className="w-2 h-2 bg-red-500 rounded-full animate-pulse" />
          <span>Live reactions</span>
        </div>
      </div>
    </div>
  );
}

// Hook to trigger reactions from external components
export function useSocialInteractions() {
  const triggerReaction = useCallback((type: string) => {
    window.dispatchEvent(new CustomEvent('trigger-reaction', { 
      detail: { type } 
    }));
  }, []);

  const triggerComment = useCallback((text: string) => {
    window.dispatchEvent(new CustomEvent('trigger-comment', { 
      detail: { text } 
    }));
  }, []);

  return { triggerReaction, triggerComment };
}

