import { useRef, useState, useEffect, useCallback } from "react";
import { Button } from "@/components/ui/button";
import { Slider } from "@/components/ui/slider";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Play, Pause, Volume2, VolumeX, Maximize, Minimize, 
  Settings, SkipBack, SkipForward, RotateCcw 
} from "lucide-react";

interface StreamPlayerProps {
  src?: string;
  streamUrl?: string;
  isLive?: boolean;
  title?: string;
  streamer?: string;
  viewers?: number;
  quality?: 'auto' | 'low' | 'medium' | 'high' | 'ultra';
  onQualityChange?: (quality: string) => void;
  enableChapters?: boolean;
  enableEffects?: boolean;
}

export default function StreamPlayer({
  src,
  streamUrl,
  isLive = false,
  title,
  streamer,
  viewers,
  quality = 'auto',
  onQualityChange,
  enableChapters = true,
  enableEffects = true
}: StreamPlayerProps) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [isFullscreen, setIsFullscreen] = useState(false);
  const [volume, setVolume] = useState([75]);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [buffered, setBuffered] = useState(0);
  const [isLoading, setIsLoading] = useState(false);
  const [showControls, setShowControls] = useState(true);
  const [bitrate, setBitrate] = useState(0);
  const [fps, setFps] = useState(0);

  const videoRef = useRef<HTMLVideoElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const controlsTimeoutRef = useRef<NodeJS.Timeout>();

  // Quality settings
  const qualityOptions = [
    { value: 'auto', label: 'Auto', bitrate: 0 },
    { value: 'low', label: '360p', bitrate: 500 },
    { value: 'medium', label: '480p', bitrate: 1000 },
    { value: 'high', label: '720p', bitrate: 2500 },
    { value: 'ultra', label: '1080p', bitrate: 5000 }
  ];

  // Initialize video
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const handleLoadStart = () => setIsLoading(true);
    const handleLoadedData = () => {
      setIsLoading(false);
      if (!isLive) {
        setDuration(video.duration);
      }
    };

    const handleTimeUpdate = () => {
      setCurrentTime(video.currentTime);
      updateBuffered();
    };

    const handleProgress = () => updateBuffered();

    const handleCanPlay = () => setIsLoading(false);

    const handleWaiting = () => setIsLoading(true);

    const handleError = (e: Event) => {
      console.error('Video error:', e);
      setIsLoading(false);
    };

    // Add event listeners
    video.addEventListener('loadstart', handleLoadStart);
    video.addEventListener('loadeddata', handleLoadedData);
    video.addEventListener('timeupdate', handleTimeUpdate);
    video.addEventListener('progress', handleProgress);
    video.addEventListener('canplay', handleCanPlay);
    video.addEventListener('waiting', handleWaiting);
    video.addEventListener('error', handleError);

    return () => {
      video.removeEventListener('loadstart', handleLoadStart);
      video.removeEventListener('loadeddata', handleLoadedData);
      video.removeEventListener('timeupdate', handleTimeUpdate);
      video.removeEventListener('progress', handleProgress);
      video.removeEventListener('canplay', handleCanPlay);
      video.removeEventListener('waiting', handleWaiting);
      video.removeEventListener('error', handleError);
    };
  }, [isLive]);

  // Update buffered progress
  const updateBuffered = useCallback(() => {
    const video = videoRef.current;
    if (!video || !video.buffered.length) return;

    const bufferedEnd = video.buffered.end(video.buffered.length - 1);
    const bufferedPercent = (bufferedEnd / video.duration) * 100;
    setBuffered(bufferedPercent);
  }, []);

  // Monitor stream quality
  useEffect(() => {
    if (!videoRef.current) return;

    const video = videoRef.current;
    const updateStats = () => {
      // Get video quality stats (approximation)
      if (video.videoWidth && video.videoHeight) {
        const estimatedBitrate = quality === 'auto' ? 
          Math.round((video.videoWidth * video.videoHeight * 30) / 1000) :
          qualityOptions.find(q => q.value === quality)?.bitrate || 0;
        setBitrate(estimatedBitrate);
      }

      // Estimate FPS (simplified)
      setFps(isLive ? 30 : 24);
    };

    const interval = setInterval(updateStats, 2000);
    return () => clearInterval(interval);
  }, [quality, isLive]);

  // Hide controls after inactivity
  const resetControlsTimeout = useCallback(() => {
    setShowControls(true);

    if (controlsTimeoutRef.current) {
      clearTimeout(controlsTimeoutRef.current);
    }

    if (isPlaying) {
      controlsTimeoutRef.current = setTimeout(() => {
        setShowControls(false);
      }, 3000);
    }
  }, [isPlaying]);

  // Play/pause
  const togglePlay = useCallback(() => {
    const video = videoRef.current;
    if (!video) return;

    if (isPlaying) {
      video.pause();
    } else {
      video.play();
    }
    setIsPlaying(!isPlaying);
  }, [isPlaying]);

  // Seek
  const handleSeek = useCallback((value: number[]) => {
    const video = videoRef.current;
    if (!video || isLive) return;

    const seekTime = (value[0] / 100) * duration;
    video.currentTime = seekTime;
    setCurrentTime(seekTime);
  }, [duration, isLive]);

  // Volume control
  const handleVolumeChange = useCallback((value: number[]) => {
    const video = videoRef.current;
    if (!video) return;

    setVolume(value);
    video.volume = value[0] / 100;
    setIsMuted(value[0] === 0);
  }, []);

  // Toggle mute
  const toggleMute = useCallback(() => {
    const video = videoRef.current;
    if (!video) return;

    if (isMuted) {
      video.volume = volume[0] / 100;
      setIsMuted(false);
    } else {
      video.volume = 0;
      setIsMuted(true);
    }
  }, [isMuted, volume]);

  // Skip functions
  const skipBackward = useCallback(() => {
    const video = videoRef.current;
    if (!video || isLive) return;
    video.currentTime = Math.max(0, video.currentTime - 10);
  }, [isLive]);

  const skipForward = useCallback(() => {
    const video = videoRef.current;
    if (!video || isLive) return;
    video.currentTime = Math.min(duration, video.currentTime + 10);
  }, [duration, isLive]);

  // Format time
  const formatTime = (time: number) => {
    const hours = Math.floor(time / 3600);
    const minutes = Math.floor((time % 3600) / 60);
    const seconds = Math.floor(time % 60);

    if (hours > 0) {
      return `${hours}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
    }
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  // Progress percentage
  const progressPercent = duration > 0 ? (currentTime / duration) * 100 : 0;

  return (
    <Card 
      ref={containerRef}
      className="bg-dark-200 border-dark-400 overflow-hidden relative group"
      onMouseMove={resetControlsTimeout}
      onMouseEnter={resetControlsTimeout}
      data-testid="stream-player"
    >
      {/* Video Element */}
      <div className="relative aspect-video bg-black">
        <video
          ref={videoRef}
          src={src || streamUrl}
          className="w-full h-full object-contain"
          onClick={togglePlay}
          autoPlay={isLive}
          muted={isMuted}
          playsInline
          data-testid="video-element"
        />

        {/* Loading Spinner */}
        {isLoading && (
          <div className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-50">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-500"></div>
          </div>
        )}

        {/* Live Badge */}
        {isLive && (
          <div className="absolute top-4 left-4">
            <Badge className="bg-red-500 text-white animate-pulse">
              LIVE
            </Badge>
          </div>
        )}

        {/* Stream Info */}
        {(title || streamer) && (
          <div className="absolute top-4 right-4 text-right">
            {title && (
              <div className="text-white font-semibold bg-black bg-opacity-50 px-2 py-1 rounded text-sm">
                {title}
              </div>
            )}
            {streamer && (
              <div className="text-gray-300 text-xs bg-black bg-opacity-50 px-2 py-1 rounded mt-1">
                {streamer}
              </div>
            )}
          </div>
        )}

        {/* Viewer Count */}
        {viewers && (
          <div className="absolute bottom-4 right-4">
            <Badge variant="outline" className="bg-black bg-opacity-50 text-white border-gray-600">
              {viewers.toLocaleString()} viewers
            </Badge>
          </div>
        )}

        {/* Controls Overlay */}
        <div 
          className={`absolute inset-0 transition-opacity duration-300 ${
            showControls ? 'opacity-100' : 'opacity-0'
          }`}
        >
          {/* Progress Bar */}
          {!isLive && (
            <div className="absolute bottom-20 left-4 right-4">
              <div className="space-y-1">
                {/* Buffered progress */}
                <div className="w-full bg-gray-600 rounded-full h-1">
                  <div 
                    className="bg-gray-400 h-1 rounded-full transition-all duration-300"
                    style={{ width: `${buffered}%` }}
                  />
                  <div 
                    className="bg-purple-500 h-1 rounded-full transition-all duration-100"
                    style={{ width: `${progressPercent}%`, marginTop: '-4px' }}
                  />
                </div>

                {/* Time display */}
                <div className="flex justify-between text-xs text-white">
                  <span>{formatTime(currentTime)}</span>
                  <span>{formatTime(duration)}</span>
                </div>
              </div>
            </div>
          )}

          {/* Main Controls */}
          <div className="absolute bottom-4 left-4 right-4 flex items-center justify-between">
            <div className="flex items-center space-x-3">
              {/* Play/Pause */}
              <Button
                onClick={togglePlay}
                size="icon"
                className="bg-purple-500 hover:bg-purple-600 text-white"
                data-testid="play-pause-button"
              >
                {isPlaying ? <Pause className="w-5 h-5" /> : <Play className="w-5 h-5" />}
              </Button>

              {/* Skip Controls (not for live) */}
              {!isLive && (
                <>
                  <Button
                    onClick={skipBackward}
                    size="icon"
                    variant="ghost"
                    className="text-white hover:text-purple-500"
                    data-testid="skip-backward"
                  >
                    <SkipBack className="w-4 h-4" />
                  </Button>

                  <Button
                    onClick={skipForward}
                    size="icon"
                    variant="ghost"
                    className="text-white hover:text-purple-500"
                    data-testid="skip-forward"
                  >
                    <SkipForward className="w-4 h-4" />
                  </Button>
                </>
              )}

              {/* Volume */}
              <div className="flex items-center space-x-2">
                <Button
                  onClick={toggleMute}
                  size="icon"
                  variant="ghost"
                  className="text-white hover:text-purple-500"
                  data-testid="mute-button"
                >
                  {isMuted ? <VolumeX className="w-4 h-4" /> : <Volume2 className="w-4 h-4" />}
                </Button>

                <div className="w-20">
                  <Slider
                    value={volume}
                    onValueChange={handleVolumeChange}
                    max={100}
                    step={1}
                    className="w-full"
                    data-testid="volume-slider"
                  />
                </div>
              </div>
            </div>

            <div className="flex items-center space-x-3">
              {/* Quality Badge */}
              <Badge variant="outline" className="text-xs text-white border-gray-600">
                {quality === 'auto' ? 'AUTO' : qualityOptions.find(q => q.value === quality)?.label}
              </Badge>

              {/* Stream Stats */}
              {(bitrate > 0 || fps > 0) && (
                <div className="text-xs text-gray-300 bg-black bg-opacity-50 px-2 py-1 rounded">
                  {bitrate > 0 && <span>{bitrate}kbps</span>}
                  {fps > 0 && <span className="ml-2">{fps}fps</span>}
                </div>
              )}

              {/* Settings */}
              <Button
                size="icon"
                variant="ghost"
                className="text-white hover:text-purple-500"
                data-testid="settings-button"
              >
                <Settings className="w-4 h-4" />
              </Button>

              {/* Fullscreen */}
              <Button
                onClick={() => setIsFullscreen(!isFullscreen)}
                size="icon"
                variant="ghost"
                className="text-white hover:text-purple-500"
                data-testid="fullscreen-button"
              >
                {isFullscreen ? <Minimize className="w-4 h-4" /> : <Maximize className="w-4 h-4" />}
              </Button>
            </div>
          </div>
        </div>
      </div>
    </Card>
  );
}

client/src/components/video-streamer.tsx.content:
import { useRef, useState, useEffect, useCallback } from "react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Video, VideoOff, Camera, Settings, Radio, RotateCcw } from "lucide-react";

interface VideoStreamerProps {
  isStreaming?: boolean;
  onStreamStart?: (stream: MediaStream) => void;
  onStreamEnd?: () => void;
  quality?: 'low' | 'medium' | 'high' | 'ultra';
  enableEffects?: boolean;
  roomId?: string;
}

export default function VideoStreamer({
  isStreaming = false,
  onStreamStart,
  onStreamEnd,
  quality = 'high',
  enableEffects = true,
  roomId
}: VideoStreamerProps) {
  const [isRecording, setIsRecording] = useState(false);
  const [isVideoOn, setIsVideoOn] = useState(true);
  const [selectedCamera, setSelectedCamera] = useState<string>('');
  const [cameras, setCameras] = useState<MediaDeviceInfo[]>([]);
  const [resolution, setResolution] = useState({ width: 854, height: 480 }); // Mobile default
  const [frameRate, setFrameRate] = useState(24); // Lower for mobile
  const [bitrate, setBitrate] = useState(1000); // Mobile optimized
  const [facingMode, setFacingMode] = useState<'user' | 'environment'>('user');
  const [isMobileDevice, setIsMobileDevice] = useState(false);
  const [isPortrait, setIsPortrait] = useState(false);

  const videoRef = useRef<HTMLVideoElement>(null);
  const streamRef = useRef<MediaStream | null>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const contextRef = useRef<CanvasRenderingContext2D | null>(null);

  // Mobile-optimized video quality settings
  const getVideoConstraints = useCallback(() => {
    const qualitySettings = isMobileDevice ? {
      low: { width: 480, height: 360, frameRate: 15, bitrate: 300 }, // Very mobile friendly
      medium: { width: 640, height: 480, frameRate: 20, bitrate: 600 }, // Standard mobile
      high: { width: 854, height: 480, frameRate: 24, bitrate: 1000 }, // WiFi mobile
      ultra: { width: 1280, height: 720, frameRate: 30, bitrate: 1500 } // Mobile max
    } : {
      low: { width: 640, height: 360, frameRate: 15, bitrate: 500 },
      medium: { width: 854, height: 480, frameRate: 24, bitrate: 1000 },
      high: { width: 1280, height: 720, frameRate: 30, bitrate: 2500 },
      ultra: { width: 1920, height: 1080, frameRate: 60, bitrate: 5000 }
    };

    const settings = qualitySettings[quality];
    setResolution({ width: settings.width, height: settings.height });
    setFrameRate(settings.frameRate);
    setBitrate(settings.bitrate);

    return {
      video: {
        deviceId: selectedCamera ? { exact: selectedCamera } : undefined,
        width: { ideal: isPortrait ? settings.height : settings.width },
        height: { ideal: isPortrait ? settings.width : settings.height },
        frameRate: { ideal: settings.frameRate, max: settings.frameRate },
        facingMode: isMobileDevice ? facingMode : 'user'
      },
      audio: false // Audio handled separately by AudioStreamer
    };
  }, [quality, selectedCamera, isMobileDevice, facingMode, isPortrait]);

  // Get available cameras
  const getAvailableCameras = useCallback(async () => {
    try {
      const devices = await navigator.mediaDevices.enumerateDevices();
      const videoDevices = devices.filter(device => device.kind === 'videoinput');
      setCameras(videoDevices);

      if (videoDevices.length > 0 && !selectedCamera) {
        setSelectedCamera(videoDevices[0].deviceId);
      }
    } catch (error) {
      console.error('Failed to get cameras:', error);
    }
  }, [selectedCamera]);

  // Initialize video effects canvas
  const initializeVideoProcessing = useCallback(() => {
    if (canvasRef.current && videoRef.current) {
      const canvas = canvasRef.current;
      const video = videoRef.current;

      canvas.width = resolution.width;
      canvas.height = resolution.height;
      contextRef.current = canvas.getContext('2d');

      if (enableEffects && contextRef.current) {
        // Setup real-time video processing
        const processFrame = () => {
          if (contextRef.current && video && !video.paused && !video.ended) {
            contextRef.current.drawImage(video, 0, 0, canvas.width, canvas.height);

            // Apply video effects here (filters, overlays, etc.)
            if (enableEffects) {
              const imageData = contextRef.current.getImageData(0, 0, canvas.width, canvas.height);
              // Example: Apply basic processing
              contextRef.current.putImageData(imageData, 0, 0);
            }
          }

          if (isRecording) {
            requestAnimationFrame(processFrame);
          }
        };

        video.addEventListener('play', processFrame);
      }
    }
  }, [resolution, enableEffects, isRecording]);

  // Start video streaming
  const startStreaming = useCallback(async () => {
    try {
      const constraints = getVideoConstraints();
      const stream = await navigator.mediaDevices.getUserMedia(constraints);

      streamRef.current = stream;
      setIsRecording(true);

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
      }

      initializeVideoProcessing();
      onStreamStart?.(stream);

    } catch (error) {
      console.error('Failed to start video stream:', error);
    }
  }, [getVideoConstraints, initializeVideoProcessing, onStreamStart]);

  // Stop video streaming
  const stopStreaming = useCallback(() => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }

    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }

    setIsRecording(false);
    onStreamEnd?.();
  }, [onStreamEnd]);

  // Toggle video
  const toggleVideo = useCallback(() => {
    if (streamRef.current) {
      const videoTrack = streamRef.current.getVideoTracks()[0];
      if (videoTrack) {
        videoTrack.enabled = !isVideoOn;
        setIsVideoOn(!isVideoOn);
      }
    }
  }, [isVideoOn]);

  // Switch camera
  const switchCamera = useCallback(async (deviceId: string) => {
    setSelectedCamera(deviceId);

    if (isRecording) {
      // Restart stream with new camera
      stopStreaming();
      setTimeout(() => {
        startStreaming();
      }, 500);
    }
  }, [isRecording, stopStreaming, startStreaming]);

  // Mobile device detection and orientation handling
  useEffect(() => {
    const checkMobileAndOrientation = () => {
      const isMobile = /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) ||
                       window.innerWidth <= 768;
      setIsMobileDevice(isMobile);
      setIsPortrait(window.innerHeight > window.innerWidth);
    };

    checkMobileAndOrientation();
    window.addEventListener('resize', checkMobileAndOrientation);
    window.addEventListener('orientationchange', checkMobileAndOrientation);

    return () => {
      window.removeEventListener('resize', checkMobileAndOrientation);
      window.removeEventListener('orientationchange', checkMobileAndOrientation);
    };
  }, []);

  // Initialize cameras on mount
  useEffect(() => {
    getAvailableCameras();
  }, [getAvailableCameras]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopStreaming();
    };
  }, [stopStreaming]);

  return (
    <Card className="bg-dark-200 border-dark-400 p-6">
      <div className="space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Radio className={`w-5 h-5 ${isRecording ? 'text-red-500 animate-pulse' : 'text-gray-400'}`} />
            <h3 className="text-lg font-semibold text-white">Video Stream</h3>
            {roomId && (
              <Badge variant="outline" className="text-xs">
                Room: {roomId}
              </Badge>
            )}
          </div>
          <Badge 
            className={`${isRecording ? 'bg-red-500' : 'bg-gray-500'} text-white`}
            data-testid="video-stream-status"
          >
            {isRecording ? 'LIVE' : 'OFFLINE'}
          </Badge>
        </div>

        {/* Video Preview */}
        <div className="relative aspect-video bg-dark-400 rounded-lg overflow-hidden">
          <video
            ref={videoRef}
            autoPlay
            muted
            playsInline
            className={`w-full h-full object-cover ${!isVideoOn ? 'opacity-0' : ''}`}
            data-testid="video-preview"
          />
          {enableEffects && (
            <canvas
              ref={canvasRef}
              className="absolute inset-0 w-full h-full object-cover"
              style={{ display: isRecording && enableEffects ? 'block' : 'none' }}
            />
          )}

          {!isVideoOn && (
            <div className="absolute inset-0 flex items-center justify-center bg-dark-300">
              <VideoOff className="w-16 h-16 text-gray-500" />
            </div>
          )}

          {/* Video overlay info */}
          {isRecording && (
            <div className="absolute top-4 left-4 space-y-1">
              <Badge className="bg-red-500 text-white text-xs">
                LIVE
              </Badge>
              <div className="text-xs text-white bg-black bg-opacity-50 px-2 py-1 rounded">
                {resolution.width}x{resolution.height} â€¢ {frameRate}fps
              </div>
            </div>
          )}
        </div>

        {/* Camera Selection - Mobile Optimized */}
        <div className="space-y-3">
          <label className="text-sm text-gray-400">Camera</label>

          {isMobileDevice ? (
            // Mobile camera switching (front/back)
            <div className="flex gap-2">
              <Button
                onClick={() => setFacingMode('user')}
                variant={facingMode === 'user' ? 'default' : 'outline'}
                className={`flex-1 h-12 ${
                  facingMode === 'user' 
                    ? 'bg-purple-500 text-white' 
                    : 'border-dark-400 text-white'
                }`}
                data-testid="front-camera"
              >
                <Camera className="w-5 h-5 mr-2" />
                Front
              </Button>
              <Button
                onClick={() => setFacingMode('environment')}
                variant={facingMode === 'environment' ? 'default' : 'outline'}
                className={`flex-1 h-12 ${
                  facingMode === 'environment' 
                    ? 'bg-purple-500 text-white' 
                    : 'border-dark-400 text-white'
                }`}
                data-testid="back-camera"
              >
                <Camera className="w-5 h-5 mr-2" />
                Back
              </Button>
            </div>
          ) : (
            // Desktop camera selection
            <div className="flex items-center space-x-2">
              <Select value={selectedCamera} onValueChange={switchCamera}>
                <SelectTrigger className="flex-1 bg-dark-300 border-dark-400 text-white">
                  <SelectValue placeholder="Select camera" />
                </SelectTrigger>
                <SelectContent className="bg-dark-300 border-dark-400">
                  {cameras.map((camera) => (
                    <SelectItem key={camera.deviceId} value={camera.deviceId}>
                      {camera.label || `Camera ${cameras.indexOf(camera) + 1}`}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <Button
                variant="outline"
                size="icon"
                className="border-dark-400 text-white"
                onClick={() => getAvailableCameras()}
                data-testid="refresh-cameras"
              >
                <RotateCcw className="w-4 h-4" />
              </Button>
            </div>
          )}

          {isMobileDevice && (
            <div className="text-xs text-gray-400 space-y-1">
              <div>Orientation: {isPortrait ? 'Portrait' : 'Landscape'}</div>
              <div>Resolution: {resolution.width}x{resolution.height}</div>
            </div>
          )}
        </div>

        {/* Quality Settings */}
        <div className="grid grid-cols-3 gap-4 text-sm">
          <div>
            <span className="text-gray-400">Quality:</span>
            <div className="text-white font-medium">{quality.toUpperCase()}</div>
          </div>
          <div>
            <span className="text-gray-400">Resolution:</span>
            <div className="text-white font-medium">{resolution.width}x{resolution.height}</div>
          </div>
          <div>
            <span className="text-gray-400">Bitrate:</span>
            <div className="text-white font-medium">{bitrate}kbps</div>
          </div>
        </div>

        {/* Stream Controls - Mobile Touch Friendly */}
        <div className="flex flex-col sm:flex-row gap-3">
          <Button
            onClick={isRecording ? stopStreaming : startStreaming}
            className={`${
              isRecording 
                ? 'bg-red-500 hover:bg-red-600' 
                : 'bg-purple-500 hover:bg-purple-600'
            } text-white font-medium h-12 text-base flex-1 sm:flex-initial`}
            data-testid="video-stream-toggle"
          >
            {isRecording ? 'Stop Stream' : 'Start Stream'}
          </Button>

          <div className="flex gap-2">
            <Button
              onClick={toggleVideo}
              variant="outline"
              className={`border-dark-400 ${!isVideoOn ? 'text-red-500' : 'text-white'} h-12 px-4 flex-1 sm:flex-initial`}
              disabled={!isRecording}
              data-testid="video-toggle"
            >
              {isVideoOn ? <Video className="w-5 h-5 mr-2" /> : <VideoOff className="w-5 h-5 mr-2" />}
              {isVideoOn ? 'Video' : 'No Video'}
            </Button>

            <Button
              variant="outline"
              className="border-dark-400 text-white h-12 px-4"
              data-testid="video-settings"
            >
              <Settings className="w-5 h-5" />
            </Button>
          </div>
        </div>

        {/* Stream Info */}
        {isRecording && (
          <div className="text-xs text-gray-400 space-y-1">
            <div>Resolution: {resolution.width}x{resolution.height}</div>
            <div>Frame Rate: {frameRate}fps</div>
            <div>Bitrate: {bitrate}kbps</div>
            <div>Codec: H.264</div>
            {enableEffects && <div>Effects: Enabled</div>}
          </div>
        )}
      </div>
    </Card>
  );
}

