import { useState, useEffect } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Search, Filter, Plus, Trophy, Zap } from 'lucide-react';
import { useBattles, useActiveBattles } from '@/hooks/useApi';
import { useWebSocket } from '@/hooks/useWebSocket';
import { useAuth } from '@/hooks/useAuth';
import BattleCard from './battle-card';
import CreateBattleDialog from './create-battle-dialog';
import LoadingSpinner from '@/components/ui/loading-spinner';
import type { Battle } from '@shared/schema';

const CATEGORIES = ['freestyle', 'championship', 'team', 'open_mic'];
const STATUSES = ['active', 'completed', 'pending'];

export default function BattleList() {
  const { user } = useAuth();
  const { subscribeToMessage } = useWebSocket();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('active');
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [liveUpdates, setLiveUpdates] = useState<Map<string, Partial<Battle>>>(new Map());

  const { data: allBattles, isLoading: loadingAll } = useBattles();
  const { data: activeBattles, isLoading: loadingActive } = useActiveBattles();

  const battles = selectedStatus === 'active' ? activeBattles : allBattles;
  const isLoading = selectedStatus === 'active' ? loadingActive : loadingAll;

  // Subscribe to real-time battle updates
  useEffect(() => {
    const unsubscribeVote = subscribeToMessage('vote-update', (data) => {
      setLiveUpdates(prev => new Map(prev.set(data.battleId, {
        contestant1Votes: data.contestant1Votes,
        contestant2Votes: data.contestant2Votes,
        totalVotes: data.totalVotes,
      })));
    });

    const unsubscribeBattleUpdate = subscribeToMessage('battle-update', (data) => {
      setLiveUpdates(prev => new Map(prev.set(data.battleId, data.updates)));
    });

    return () => {
      unsubscribeVote();
      unsubscribeBattleUpdate();
    };
  }, [subscribeToMessage]);

  const filteredBattles = battles?.filter((battle: Battle) => {
    // Apply live updates if available
    const updatedBattle = liveUpdates.has(battle.id) 
      ? { ...battle, ...liveUpdates.get(battle.id) }
      : battle;

    const matchesSearch = updatedBattle.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         updatedBattle.contestant1Name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         updatedBattle.contestant2Name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = !selectedCategory || updatedBattle.category === selectedCategory;
    const matchesStatus = !selectedStatus || updatedBattle.status === selectedStatus;

    return matchesSearch && matchesCategory && matchesStatus;
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <Trophy className="w-6 h-6 text-gold-400" />
          <h1 className="text-2xl font-bold text-white">Battles</h1>
          <Badge variant="secondary">{battles?.length || 0}</Badge>
          {selectedStatus === 'active' && (
            <div className="flex items-center space-x-1">
              <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
              <span className="text-sm text-green-400 font-medium">LIVE</span>
            </div>
          )}
        </div>

        {user && (user.role === 'artist' || user.role === 'producer') && (
          <Button
            data-testid="button-create-battle"
            onClick={() => setShowCreateDialog(true)}
            className="bg-gold-500 hover:bg-gold-600 text-black font-medium"
          >
            <Plus className="w-4 h-4 mr-2" />
            Create Battle
          </Button>
        )}
      </div>

      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-4">
        {/* Search */}
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
          <Input
            data-testid="input-search-battles"
            placeholder="Search battles or contestants..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10 bg-dark-200 border-dark-400 text-white"
          />
        </div>

        {/* Status Filter */}
        <div className="flex items-center space-x-2">
          <Filter className="w-4 h-4 text-gray-400" />
          <div className="flex space-x-2">
            {STATUSES.map((status) => (
              <Button
                key={status}
                data-testid={`filter-status-${status}`}
                variant={selectedStatus === status ? "default" : "outline"}
                size="sm"
                onClick={() => setSelectedStatus(status)}
                className="text-xs"
              >
                {status === 'active' && <Zap className="w-3 h-3 mr-1" />}
                {status.charAt(0).toUpperCase() + status.slice(1)}
              </Button>
            ))}
          </div>
        </div>

        {/* Category Filter */}
        <div className="flex flex-wrap gap-2">
          <Button
            data-testid="filter-all-categories"
            variant={!selectedCategory ? "default" : "outline"}
            size="sm"
            onClick={() => setSelectedCategory('')}
            className="text-xs"
          >
            All Categories
          </Button>
          {CATEGORIES.map((category) => (
            <Button
              key={category}
              data-testid={`filter-category-${category}`}
              variant={selectedCategory === category ? "default" : "outline"}
              size="sm"
              onClick={() => setSelectedCategory(selectedCategory === category ? '' : category)}
              className="text-xs"
            >
              {category.replace('_', ' ').toUpperCase()}
            </Button>
          ))}
        </div>
      </div>

      {/* Battle List */}
      <div className="space-y-4">
        {filteredBattles?.length === 0 ? (
          <div className="text-center py-12">
            <Trophy className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-300 mb-2">No battles found</h3>
            <p className="text-gray-500">
              {searchTerm || selectedCategory 
                ? 'Try adjusting your search or filters'
                : selectedStatus === 'active' 
                  ? 'No active battles right now. Check back soon!'
                  : 'Create the first battle to get started!'
              }
            </p>
          </div>
        ) : (
          filteredBattles?.map((battle: Battle) => {
            // Apply live updates if available
            const updatedBattle = liveUpdates.has(battle.id) 
              ? { ...battle, ...liveUpdates.get(battle.id) }
              : battle;

            return (
              <BattleCard
                key={battle.id}
                battle={updatedBattle as Battle}
                showVoting={selectedStatus === 'active'}
              />
            );
          })
        )}
      </div>

      {/* Create Battle Dialog */}
      {showCreateDialog && (
        <CreateBattleDialog
          open={showCreateDialog}
          onOpenChange={setShowCreateDialog}
        />
      )}
    </div>
  );
}

client/src/components/battles/create-bat.content:
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { insertBattleSchema } from '@shared/schema';
import { useCreateBattle, useUsers } from '@/hooks/useApi';
import { useAuth } from '@/hooks/useAuth';
import { Trophy, Calendar } from 'lucide-react';
import type { InsertBattle } from '@shared/schema';

interface CreateBattleDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

const CATEGORIES = [
  { value: 'freestyle', label: 'Freestyle Battle' },
  { value: 'championship', label: 'Championship Battle' },
  { value: 'team', label: 'Team Battle' },
  { value: 'open_mic', label: 'Open Mic Battle' },
];

export default function CreateBattleDialog({ open, onOpenChange }: CreateBattleDialogProps) {
  const { user } = useAuth();
  const createBattle = useCreateBattle();
  const { data: users } = useUsers();

  // Filter to artists and producers only
  const contestants = users?.filter(u => 
    u.role === 'artist' || u.role === 'producer'
  );

  const {
    register,
    handleSubmit,
    setValue,
    watch,
    reset,
    formState: { errors },
  } = useForm<InsertBattle>({
    resolver: zodResolver(insertBattleSchema),
    defaultValues: {
      contestant1Id: user?.id || '',
      contestant1Name: user?.displayName || '',
    },
  });

  const selectedContestant2 = watch('contestant2Id');

  const onSubmit = async (data: InsertBattle) => {
    try {
      // Set end time to 24 hours from now if not specified
      if (!data.endTime) {
        const endTime = new Date();
        endTime.setHours(endTime.getHours() + 24);
        data.endTime = endTime;
      }

      await createBattle.mutateAsync(data);
      reset();
      onOpenChange(false);
    } catch (error) {
      console.error('Failed to create battle:', error);
    }
  };

  const handleContestant2Change = (userId: string) => {
    const selectedUser = contestants?.find(u => u.id === userId);
    if (selectedUser) {
      setValue('contestant2Id', userId);
      setValue('contestant2Name', selectedUser.displayName);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md bg-dark-200 border-dark-400">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Trophy className="w-5 h-5 text-gold-400" />
            <span>Create New Battle</span>
          </DialogTitle>
          <DialogDescription>
            Challenge another artist to an epic battle
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          {/* Battle Title */}
          <div className="space-y-2">
            <Label htmlFor="title">Battle Title*</Label>
            <Input
              id="title"
              data-testid="input-battle-title"
              {...register('title')}
              placeholder="Epic Rap Battle of History"
              className="bg-dark-300 border-dark-400"
            />
            {errors.title && (
              <p className="text-red-400 text-sm">{errors.title.message}</p>
            )}
          </div>

          {/* Category */}
          <div className="space-y-2">
            <Label htmlFor="category">Battle Category*</Label>
            <Select onValueChange={(value) => setValue('category', value)} data-testid="select-battle-category">
              <SelectTrigger>
                <SelectValue placeholder="Select battle type" />
              </SelectTrigger>
              <SelectContent>
                {CATEGORIES.map((category) => (
                  <SelectItem key={category.value} value={category.value}>
                    {category.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {errors.category && (
              <p className="text-red-400 text-sm">{errors.category.message}</p>
            )}
          </div>

          {/* Contestants */}
          <div className="space-y-4">
            <Label>Contestants</Label>

            {/* Contestant 1 (You) */}
            <div className="flex items-center space-x-3 p-3 bg-dark-300 rounded-lg">
              <div className="w-8 h-8 bg-gradient-to-br from-purple-500 to-electric-500 rounded-full flex items-center justify-center">
                <span className="text-white text-sm font-bold">
                  {user?.displayName?.charAt(0)}
                </span>
              </div>
              <div className="flex-1">
                <p className="font-medium text-white">{user?.displayName}</p>
                <p className="text-sm text-gray-400">You â€¢ {user?.role}</p>
              </div>
              <Badge className="bg-purple-500">Challenger</Badge>
            </div>

            {/* VS Divider */}
            <div className="flex items-center justify-center">
              <div className="bg-gradient-to-r from-gold-400 to-gold-600 text-black px-3 py-1 rounded-full font-bold text-sm">
                VS
              </div>
            </div>

            {/* Contestant 2 */}
            <div className="space-y-2">
              <Label htmlFor="opponent">Choose Your Opponent*</Label>
              <Select onValueChange={handleContestant2Change} data-testid="select-opponent">
                <SelectTrigger>
                  <SelectValue placeholder="Select opponent" />
                </SelectTrigger>
                <SelectContent>
                  {contestants?.filter(u => u.id !== user?.id).map((contestant) => (
                    <SelectItem key={contestant.id} value={contestant.id}>
                      <div className="flex items-center space-x-2">
                        <span>{contestant.displayName}</span>
                        <Badge variant="outline" className="ml-2">
                          {contestant.role}
                        </Badge>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              {errors.contestant2Id && (
                <p className="text-red-400 text-sm">{errors.contestant2Id.message}</p>
              )}
            </div>
          </div>

          {/* Battle Duration */}
          <div className="space-y-2">
            <Label htmlFor="endTime">Battle End Time</Label>
            <div className="flex items-center space-x-2">
              <Calendar className="w-4 h-4 text-gray-400" />
              <Input
                id="endTime"
                data-testid="input-battle-end-time"
                {...register('endTime', { 
                  setValueAs: (value) => value ? new Date(value) : undefined 
                })}
                type="datetime-local"
                className="bg-dark-300 border-dark-400"
              />
            </div>
            <p className="text-xs text-gray-500">
              Leave empty to set battle duration to 24 hours
            </p>
          </div>

          {/* Form Actions */}
          <div className="flex justify-end space-x-3">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              data-testid="button-cancel"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={createBattle.isPending || !selectedContestant2}
              className="bg-gold-500 hover:bg-gold-600 text-black font-medium"
              data-testid="button-create-battle"
            >
              {createBattle.isPending ? 'Creating...' : 'Create Battle'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/beats/beat-card.ts.content:
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Play, Pause, Heart, ShoppingCart, Download, DollarSign, Clock } from 'lucide-react';
import { useState } from 'react';
import { useUpdateBeatPlays, useUpdateBeatLikes, usePurchaseBeat } from '@/hooks/useApi';
import { useAuth } from '@/hooks/useAuth';
import { cn } from '@/lib/utils';
import type { Beat } from '@shared/schema';

interface BeatCardProps {
  beat: Beat;
  isPlaying?: boolean;
  onPlayToggle?: () => void;
  className?: string;
}

export default function BeatCard({ beat, isPlaying = false, onPlayToggle, className }: BeatCardProps) {
  const { user } = useAuth();
  const [isLiked, setIsLiked] = useState(false);
  const updatePlays = useUpdateBeatPlays();
  const updateLikes = useUpdateBeatLikes();
  const purchaseBeat = usePurchaseBeat();

  const handlePlayToggle = () => {
    if (!isPlaying && onPlayToggle) {
      updatePlays.mutate(beat.id);
    }
    onPlayToggle?.();
  };

  const handleLike = () => {
    updateLikes.mutate(beat.id);
    setIsLiked(!isLiked);
  };

  const handlePurchase = () => {
    purchaseBeat.mutate(beat.id);
  };

  const formatDuration = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getLicenseColor = (licenseType: string) => {
    switch (licenseType) {
      case 'exclusive':
        return 'bg-gold-500';
      case 'premium':
        return 'bg-purple-500';
      case 'basic':
        return 'bg-blue-500';
      default:
        return 'bg-gray-500';
    }
  };

  return (
    <Card className={cn("group hover:shadow-lg transition-all duration-200 bg-dark-200 border-dark-400", className)}>
      <CardContent className="p-4">
        <div className="flex items-center space-x-4">
          {/* Beat Artwork */}
          <div className="relative">
            {beat.coverImage ? (
              <img
                src={beat.coverImage}
                alt={beat.title}
                className="w-16 h-16 rounded-lg object-cover"
              />
            ) : (
              <div className="w-16 h-16 rounded-lg bg-gradient-to-br from-gold-400 to-electric-500 flex items-center justify-center">
                <Play className="text-white w-6 h-6" />
              </div>
            )}

            {/* Play/Pause Overlay */}
            <Button
              data-testid={`button-play-${beat.id}`}
              size="icon"
              variant="ghost"
              className="absolute inset-0 bg-black/60 hover:bg-black/80 text-white opacity-0 group-hover:opacity-100 transition-opacity"
              onClick={handlePlayToggle}
            >
              {isPlaying ? (
                <Pause className="w-5 h-5" />
              ) : (
                <Play className="w-5 h-5" />
              )}
            </Button>
          </div>

          {/* Beat Info */}
          <div className="flex-1 min-w-0">
            <h3 className="font-semibold text-white truncate" data-testid={`text-beat-title-${beat.id}`}>
              {beat.title}
            </h3>
            <p className="text-sm text-gray-400 truncate" data-testid={`text-producer-${beat.id}`}>
              by {beat.producerName}
            </p>

            {/* Beat Metadata */}
            <div className="flex items-center space-x-4 mt-2">
              {beat.genre && (
                <Badge variant="secondary" className="text-xs">
                  {beat.genre}
                </Badge>
              )}
              {beat.bpm && (
                <span className="text-xs text-gray-500">
                  {beat.bpm} BPM
                </span>
              )}
            </div>
          </div>

          {/* Price & License */}
          <div className="flex flex-col items-end space-y-2">
            <div className="flex items-center space-x-2">
              <DollarSign className="w-4 h-4 text-green-400" />
              <span className="text-lg font-bold text-green-400" data-testid={`text-price-${beat.id}`}>
                ${beat.price}
              </span>
            </div>

            <Badge className={cn("text-xs", getLicenseColor(beat.licenseType))}>
              {beat.licenseType.toUpperCase()}
            </Badge>
          </div>

          {/* Beat Actions */}
          <div className="flex flex-col space-y-2">
            {/* Stats */}
            <div className="flex items-center space-x-3 text-xs text-gray-500">
              <span data-testid={`text-plays-${beat.id}`}>
                {beat.plays?.toLocaleString() || 0} plays
              </span>
              <span data-testid={`text-likes-${beat.id}`}>
                {beat.likes?.toLocaleString() || 0} likes
              </span>
              <span data-testid={`text-purchases-${beat.id}`}>
                {beat.purchases?.toLocaleString() || 0} sales
              </span>
            </div>

            {/* Action Buttons */}
            <div className="flex items-center space-x-1">
              <Button
                data-testid={`button-like-${beat.id}`}
                size="icon"
                variant="ghost"
                className={cn(
                  "text-gray-400 hover:text-red-400",
                  isLiked && "text-red-400"
                )}
                onClick={handleLike}
              >
                <Heart className={cn("w-4 h-4", isLiked && "fill-current")} />
              </Button>

              {user && user.id !== beat.producerId && (
                <Button
                  data-testid={`button-purchase-${beat.id}`}
                  size="sm"
                  variant="default"
                  className="bg-green-500 hover:bg-green-600"
                  onClick={handlePurchase}
                  disabled={purchaseBeat.isPending}
                >
                  {purchaseBeat.isPending ? (
                    <Download className="w-3 h-3 mr-1 animate-spin" />
                  ) : (
                    <ShoppingCart className="w-3 h-3 mr-1" />
                  )}
                  Buy
                </Button>
              )}

              {user && user.id === beat.producerId && (
                <Badge variant="outline" className="text-xs">
                  Your Beat
                </Badge>
              )}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/beats/beat-list.ts.content:
import { useState } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Search, Filter, Plus, Music4, TrendingUp, DollarSign } from 'lucide-react';
import { useBeats } from '@/hooks/useApi';
import { useAuth } from '@/hooks/useAuth';
import BeatCard from './beat-card';
import CreateBeatDialog from './create-beat-dialog';
import LoadingSpinner from '@/components/ui/loading-spinner';
import type { Beat } from '@shared/schema';

const GENRES = ['Hip Hop', 'R&B', 'Pop', 'Electronic', 'Rock', 'Jazz', 'Country', 'Reggae'];
const LICENSE_TYPES = ['basic', 'premium', 'exclusive'];
const SORT_OPTIONS = [
  { value: 'newest', label: 'Newest' },
  { value: 'popular', label: 'Most Popular' },
  { value: 'price_low', label: 'Price: Low to High' },
  { value: 'price_high', label: 'Price: High to Low' },
];

export default function BeatList() {
  const { user } = useAuth();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedGenre, setSelectedGenre] = useState<string>('');
  const [selectedLicense, setSelectedLicense] = useState<string>('');
  const [sortBy, setSortBy] = useState('newest');
  const [priceRange, setPriceRange] = useState<{ min: number; max: number }>({ min: 0, max: 1000 });
  const [playingBeat, setPlayingBeat] = useState<string | null>(null);
  const [showCreateDialog, setShowCreateDialog] = useState(false);

  const { data: beats, isLoading, error } = useBeats(selectedGenre || undefined);

  const filteredAndSortedBeats = beats?.filter((beat: Beat) => {
    const matchesSearch = beat.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         beat.producerName.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesGenre = !selectedGenre || beat.genre === selectedGenre;
    const matchesLicense = !selectedLicense || beat.licenseType === selectedLicense;
    const beatPrice = parseFloat(beat.price as string) || 0;
    const matchesPrice = beatPrice >= priceRange.min && beatPrice <= priceRange.max;

    return matchesSearch && matchesGenre && matchesLicense && matchesPrice;
  })?.sort((a: Beat, b: Beat) => {
    switch (sortBy) {
      case 'popular':
        return (b.plays || 0) - (a.plays || 0);
      case 'price_low':
        return (parseFloat(a.price as string) || 0) - (parseFloat(b.price as string) || 0);
      case 'price_high':
        return (parseFloat(b.price as string) || 0) - (parseFloat(a.price as string) || 0);
      case 'newest':
      default:
        return (new Date(b.createdAt || 0).getTime()) - (new Date(a.createdAt || 0).getTime());
    }
  });

  const handlePlayToggle = (beatId: string) => {
    setPlayingBeat(playingBeat === beatId ? null : beatId);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <LoadingSpinner />
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <p className="text-red-400">Failed to load beats. Please try again.</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <Music4 className="w-6 h-6 text-gold-400" />
          <h1 className="text-2xl font-bold text-white">Beat Marketplace</h1>
          <Badge variant="secondary">{beats?.length || 0}</Badge>
        </div>

        {user && user.role === 'producer' && (
          <Button
            data-testid="button-list-beat"
            onClick={() => setShowCreateDialog(true)}
            className="bg-gold-500 hover:bg-gold-600 text-black font-medium"
          >
            <Plus className="w-4 h-4 mr-2" />
            List Beat
          </Button>
        )}
      </div>

      {/* Filters and Search */}
      <div className="space-y-4">
        {/* Search and Sort */}
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
            <Input
              data-testid="input-search-beats"
              placeholder="Search beats or producers..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 bg-dark-200 border-dark-400 text-white"
            />
          </div>

          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="px-3 py-2 bg-dark-200 border border-dark-400 rounded-md text-white text-sm"
            data-testid="select-sort-beats"
          >
            {SORT_OPTIONS.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>

        {/* Filter Row */}
        <div className="flex flex-wrap gap-4">
          {/* Genre Filter */}
          <div className="flex items-center space-x-2">
            <Filter className="w-4 h-4 text-gray-400" />
            <Button
              data-testid="filter-all-genres"
              variant={!selectedGenre ? "default" : "outline"}
              size="sm"
              onClick={() => setSelectedGenre('')}
              className="text-xs"
            >
              All Genres
            </Button>
            {GENRES.map((genre) => (
              <Button
                key={genre}
                data-testid={`filter-genre-${genre.toLowerCase().replace(' ', '-')}`}
                variant={selectedGenre === genre ? "default" : "outline"}
                size="sm"
                onClick={() => setSelectedGenre(selectedGenre === genre ? '' : genre)}
                className="text-xs"
              >
                {genre}
              </Button>
            ))}
          </div>
        </div>

        {/* License and Price Filter */}
        <div className="flex flex-wrap gap-4">
          <div className="flex items-center space-x-2">
            <span className="text-sm text-gray-400">License:</span>
            {LICENSE_TYPES.map((license) => (
              <Button
                key={license}
                data-testid={`filter-license-${license}`}
                variant={selectedLicense === license ? "default" : "outline"}
                size="sm"
                onClick={() => setSelectedLicense(selectedLicense === license ? '' : license)}
                className="text-xs"
              >
                {license.toUpperCase()}
              </Button>
            ))}
          </div>

          <div className="flex items-center space-x-2">
            <DollarSign className="w-4 h-4 text-green-400" />
            <span className="text-sm text-gray-400">Price Range:</span>
            <Input
              type="number"
              placeholder="Min"
              value={priceRange.min}
              onChange={(e) => setPriceRange(prev => ({ ...prev, min: Number(e.target.value) }))}
              className="w-20 bg-dark-200 border-dark-400 text-white text-xs"
              data-testid="input-price-min"
            />
            <span className="text-gray-400">-</span>
            <Input
              type="number"
              placeholder="Max"
              value={priceRange.max}
              onChange={(e) => setPriceRange(prev => ({ ...prev, max: Number(e.target.value) }))}
              className="w-20 bg-dark-200 border-dark-400 text-white text-xs"
              data-testid="input-price-max"
            />
          </div>
        </div>
      </div>

      {/* Stats Row */}
      <div className="flex items-center justify-between p-4 bg-dark-200 rounded-lg border border-dark-400">
        <div className="flex items-center space-x-6">
          <div className="flex items-center space-x-2">
            <TrendingUp className="w-4 h-4 text-green-400" />
            <span className="text-sm text-gray-300">
              {filteredAndSortedBeats?.length || 0} beats found
            </span>
          </div>
          <div className="flex items-center space-x-2">
            <DollarSign className="w-4 h-4 text-gold-400" />
            <span className="text-sm text-gray-300">
              Avg: ${filteredAndSortedBeats && filteredAndSortedBeats.length > 0 ? (filteredAndSortedBeats.reduce((sum, beat) => sum + (parseFloat(beat.price as string) || 0), 0) / filteredAndSortedBeats.length).toFixed(2) : '0.00'}
            </span>
          </div>
        </div>
      </div>

      {/* Beat List */}
      <div className="space-y-3">
        {filteredAndSortedBeats?.length === 0 ? (
          <div className="text-center py-12">
            <Music4 className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-300 mb-2">No beats found</h3>
            <p className="text-gray-500">
              {searchTerm || selectedGenre || selectedLicense
                ? 'Try adjusting your search or filters'
                : 'List the first beat to get started!'
              }
            </p>
          </div>
        ) : (
          filteredAndSortedBeats?.map((beat: Beat) => (
            <BeatCard
              key={beat.id}
              beat={beat}
              isPlaying={playingBeat === beat.id}
              onPlayToggle={() => handlePlayToggle(beat.id)}
            />
          ))
        )}
      </div>

      {/* Create Beat Dialog */}
      {showCreateDialog && (
        <CreateBeatDialog
          open={showCreateDialog}
          onOpenChange={setShowCreateDialog}
        />
      )}
    </div>
  );
}

client/src/components/beats/create-beat-.content:
import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { insertBeatSchema } from '@shared/schema';
import { useCreateBeat } from '@/hooks/useApi';
import { useAuth } from '@/hooks/useAuth';
import { useToast } from '@/hooks/use-toast';
import { Upload, Music4, DollarSign, Loader2 } from 'lucide-react';
import type { InsertBeat } from '@shared/schema';

interface CreateBeatDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

const GENRES = ['Hip Hop', 'R&B', 'Pop', 'Electronic', 'Rock', 'Jazz', 'Country', 'Reggae'];
const KEYS = ['C', 'C#', 'D', 'D#', 'E', 'F', 'F#', 'G', 'G#', 'A', 'A#', 'B'];
const LICENSE_TYPES = [
  { value: 'basic', label: 'Basic License', desc: 'Standard commercial use' },
  { value: 'premium', label: 'Premium License', desc: 'Enhanced commercial rights' },
  { value: 'exclusive', label: 'Exclusive License', desc: 'Full ownership transfer' },
];

export default function CreateBeatDialog({ open, onOpenChange }: CreateBeatDialogProps) {
  const { user } = useAuth();
  const createBeat = useCreateBeat();
  const { toast } = useToast();
  const [isUploading, setIsUploading] = useState(false);
  const [isUploadingAudio, setIsUploadingAudio] = useState(false);
  const [isUploadingCover, setIsUploadingCover] = useState(false);

  const {
    register,
    handleSubmit,
    setValue,
    watch,
    reset,
    formState: { errors },
  } = useForm<InsertBeat>({
    resolver: zodResolver(insertBeatSchema),
    defaultValues: {
      producerId: user?.id || '',
      producerName: user?.displayName || '',
      licenseType: 'basic',
      price: '29.99',
    },
  });

  const selectedLicenseType = watch('licenseType');

  const onSubmit = async (data: InsertBeat) => {
    try {
      setIsUploading(true);
      await createBeat.mutateAsync(data);
      reset();
      onOpenChange(false);
    } catch (error) {
      console.error('Failed to create beat:', error);
    } finally {
      setIsUploading(false);
    }
  };

  const handleAudioUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploadingAudio(true);
    try {
      const formData = new FormData();
      formData.append('audio', file);

      const response = await fetch('/api/upload/audio', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const result = await response.json();
      setValue('audioUrl', result.url);

      toast({
        title: 'Success',
        description: 'Beat audio uploaded successfully',
      });
    } catch (error) {
      console.error('Audio upload failed:', error);
      toast({
        title: 'Upload Failed',
        description: 'Failed to upload beat audio. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setIsUploadingAudio(false);
    }
  };

  const handleCoverUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploadingCover(true);
    try {
      const formData = new FormData();
      formData.append('image', file);

      const response = await fetch('/api/upload/image', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const result = await response.json();
      setValue('coverImage', result.url);

      toast({
        title: 'Success',
        description: 'Cover artwork uploaded successfully',
      });
    } catch (error) {
      console.error('Cover upload failed:', error);
      toast({
        title: 'Upload Failed',
        description: 'Failed to upload cover artwork. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setIsUploadingCover(false);
    }
  };

  const getSuggestedPrice = (licenseType: string) => {
    switch (licenseType) {
      case 'basic':
        return 29.99;
      case 'premium':
        return 99.99;
      case 'exclusive':
        return 499.99;
      default:
        return 29.99;
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-2xl bg-dark-200 border-dark-400 max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Music4 className="w-5 h-5 text-gold-400" />
            <span>List New Beat</span>
          </DialogTitle>
          <DialogDescription>
            Share your beats with artists and earn from your music
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          {/* Audio Upload */}
          <div className="space-y-2">
            <Label htmlFor="audioFile">Beat Audio File*</Label>
            <div className="border-2 border-dashed border-dark-400 rounded-lg p-6 text-center">
              <Upload className="w-8 h-8 text-gray-400 mx-auto mb-2" />
              <input
