              <Label htmlFor="audioFile" className="cursor-pointer">
                {isUploadingAudio ? (
                  <div className="flex items-center space-x-2">
                    <Loader2 className="w-4 h-4 animate-spin" />
                    <span className="text-gold-400">Uploading beat...</span>
