import { Card, CardContent, CardHeader } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Users, Clock, Play, Pause, CheckCircle, X, MessageSquare } from 'lucide-react';
import { useState } from 'react';
import { useUpdateCollaborationStatus } from '@/hooks/useApi';
import { useAuth } from '@/hooks/useAuth';
import { useWebSocket } from '@/hooks/useWebSocket';
import { cn } from '@/lib/utils';
import type { Collaboration } from '@shared/schema';

interface CollaborationCardProps {
  collaboration: Collaboration;
  className?: string;
  onJoinSession?: () => void;
}

export default function CollaborationCard({ collaboration, className, onJoinSession }: CollaborationCardProps) {
  const { user } = useAuth();
  const { joinCollaboration } = useWebSocket();
  const updateStatus = useUpdateCollaborationStatus();
  const [isPlaying, setIsPlaying] = useState(false);

  const handleStatusUpdate = (status: string) => {
    updateStatus.mutate({
      id: collaboration.id,
      status,
    });
  };

  const handleJoinSession = () => {
    joinCollaboration(collaboration.id);
    onJoinSession?.();
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active':
        return 'bg-green-500';
      case 'completed':
        return 'bg-blue-500';
      case 'pending':
        return 'bg-yellow-500';
      default:
        return 'bg-gray-500';
    }
  };

  const getRoleColor = (role: string) => {
    switch (role) {
      case 'lead':
        return 'bg-purple-500';
      case 'featured':
        return 'bg-gold-500';
      case 'producer':
        return 'bg-blue-500';
      case 'writer':
        return 'bg-green-500';
      default:
        return 'bg-gray-500';
    }
  };

  const formatDate = (date: Date) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const isInvolved = user && (
    collaboration.initiatorId === user.id || 
    collaboration.collaboratorId === user.id
  );

  const canManageStatus = user && collaboration.initiatorId === user.id;
  const isPending = collaboration.status === 'pending';
  const isActive = collaboration.status === 'active';

  return (
    <Card className={cn("bg-dark-200 border-dark-400 hover:shadow-lg transition-all duration-200", className)}>
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <h3 className="text-lg font-semibold text-white" data-testid={`text-collab-track-${collaboration.id}`}>
              {collaboration.trackTitle}
            </h3>
            <Badge className={getStatusColor(collaboration.status || 'pending')}>
              {(collaboration.status || 'pending').toUpperCase()}
            </Badge>
          </div>

          <div className="flex items-center space-x-2">
            <Badge className={getRoleColor(collaboration.role)} data-testid={`badge-role-${collaboration.id}`}>
              {collaboration.role.toUpperCase()}
            </Badge>

            <div className="flex items-center space-x-1 text-xs text-gray-400">
              <Clock className="w-3 h-3" />
              <span>{formatDate(collaboration.createdAt || new Date())}</span>
            </div>
          </div>
        </div>
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Collaborators */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            {/* Initiator */}
            <div className="flex items-center space-x-2">
              <Avatar className="w-8 h-8">
                <AvatarFallback>{collaboration.initiatorName.charAt(0)}</AvatarFallback>
              </Avatar>
              <div>
                <p className="text-sm font-medium text-white" data-testid={`text-initiator-${collaboration.id}`}>
                  {collaboration.initiatorName}
                </p>
                <p className="text-xs text-gray-400">Initiator</p>
              </div>
            </div>

            {/* Collaboration Arrow */}
            <div className="flex items-center space-x-2">
              <div className="w-6 h-0.5 bg-gradient-to-r from-purple-500 to-electric-500"></div>
              <Users className="w-4 h-4 text-purple-400" />
              <div className="w-6 h-0.5 bg-gradient-to-r from-purple-500 to-electric-500"></div>
            </div>

            {/* Collaborator */}
            <div className="flex items-center space-x-2">
              <Avatar className="w-8 h-8">
                <AvatarFallback>{collaboration.collaboratorName.charAt(0)}</AvatarFallback>
              </Avatar>
              <div>
                <p className="text-sm font-medium text-white" data-testid={`text-collaborator-${collaboration.id}`}>
                  {collaboration.collaboratorName}
                </p>
                <p className="text-xs text-gray-400">{collaboration.role}</p>
              </div>
            </div>
          </div>

          {/* Play Track */}
          {collaboration.trackId && (
            <Button
              size="sm"
              variant="outline"
              data-testid={`button-play-track-${collaboration.id}`}
              onClick={() => setIsPlaying(!isPlaying)}
            >
              {isPlaying ? <Pause className="w-3 h-3 mr-1" /> : <Play className="w-3 h-3 mr-1" />}
              Play Track
            </Button>
          )}
        </div>

        {/* Description */}
        {collaboration.description && (
          <div className="p-3 bg-dark-300 rounded-lg">
            <p className="text-sm text-gray-300" data-testid={`text-description-${collaboration.id}`}>
              {collaboration.description}
            </p>
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex items-center justify-between pt-3 border-t border-dark-400">
          <div className="flex items-center space-x-2">
            {/* Status Management for Initiator */}
            {canManageStatus && isPending && (
              <div className="flex space-x-2">
                <Button
                  size="sm"
                  variant="default"
                  className="bg-green-500 hover:bg-green-600"
                  data-testid={`button-approve-${collaboration.id}`}
                  onClick={() => handleStatusUpdate('active')}
                  disabled={updateStatus.isPending}
                >
                  <CheckCircle className="w-3 h-3 mr-1" />
                  Approve
                </Button>
                <Button
                  size="sm"
                  variant="destructive"
                  data-testid={`button-decline-${collaboration.id}`}
                  onClick={() => handleStatusUpdate('declined')}
                  disabled={updateStatus.isPending}
                >
                  <X className="w-3 h-3 mr-1" />
                  Decline
                </Button>
              </div>
            )}

            {/* Response for Collaborator */}
            {user && collaboration.collaboratorId === user.id && isPending && (
              <div className="flex space-x-2">
                <Button
                  size="sm"
                  variant="default"
                  className="bg-green-500 hover:bg-green-600"
                  data-testid={`button-accept-${collaboration.id}`}
                  onClick={() => handleStatusUpdate('active')}
                  disabled={updateStatus.isPending}
                >
                  <CheckCircle className="w-3 h-3 mr-1" />
                  Accept
                </Button>
                <Button
                  size="sm"
                  variant="destructive"
                  data-testid={`button-reject-${collaboration.id}`}
                  onClick={() => handleStatusUpdate('declined')}
                  disabled={updateStatus.isPending}
                >
                  <X className="w-3 h-3 mr-1" />
                  Reject
                </Button>
              </div>
            )}

            {/* Join Live Session */}
            {isInvolved && isActive && (
              <Button
                size="sm"
                variant="default"
                className="bg-purple-500 hover:bg-purple-600"
                data-testid={`button-join-session-${collaboration.id}`}
                onClick={handleJoinSession}
              >
                <Users className="w-3 h-3 mr-1" />
                Join Session
              </Button>
            )}
          </div>

          {/* Chat/Messages */}
          <div className="flex items-center space-x-2">
            {isInvolved && (
              <Button
                size="sm"
                variant="ghost"
                className="text-gray-400 hover:text-white"
                data-testid={`button-chat-${collaboration.id}`}
              >
                <MessageSquare className="w-4 h-4" />
              </Button>
            )}
          </div>
        </div>

        {/* Progress Indicator for Active Collaborations */}
        {isActive && (
          <div className="space-y-2">
            <div className="flex justify-between text-xs text-gray-400">
              <span>Progress</span>
              <span>75%</span>
            </div>
            <div className="w-full bg-dark-300 rounded-full h-1.5">
              <div className="bg-gradient-to-r from-purple-500 to-electric-500 h-1.5 rounded-full w-3/4"></div>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import { useState, useEffect } from 'react';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Search, Filter, Plus, Users, Zap } from 'lucide-react';
import { useCollaborations, useUserCollaborations } from '@/hooks/useApi';
import { useWebSocket } from '@/hooks/useWebSocket';
import { useAuth } from '@/hooks/useAuth';
import CollaborationCard from './collaboration-card';
import CreateCollaborationDialog from './create-collaboration-dialog';
import LoadingSpinner from '@/components/ui/loading-spinner';
import type { Collaboration } from '@shared/schema';

const STATUSES = ['all', 'pending', 'active', 'completed', 'declined'];
const ROLES = ['all', 'lead', 'featured', 'producer', 'writer'];

export default function CollaborationList() {
  const { user } = useAuth();
  const { subscribeToMessage } = useWebSocket();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('all');
  const [selectedRole, setSelectedRole] = useState('all');
  const [viewType, setViewType] = useState<'all' | 'my'>('my');
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [liveUpdates, setLiveUpdates] = useState<Map<string, Partial<Collaboration>>>(new Map());

  const { data: allCollaborations, isLoading: loadingAll } = useCollaborations();
  const { data: myCollaborations, isLoading: loadingMy } = useUserCollaborations(user?.id || '');

  const collaborations = viewType === 'all' ? allCollaborations : myCollaborations;
  const isLoading = viewType === 'all' ? loadingAll : loadingMy;

  // Subscribe to real-time collaboration updates
  useEffect(() => {
    const unsubscribeUpdate = subscribeToMessage('collaboration-update', (data) => {
      setLiveUpdates(prev => new Map(prev.set(data.collaborationId, data.updates)));
    });

    const unsubscribeStatusChange = subscribeToMessage('collaboration-status-change', (data) => {
      setLiveUpdates(prev => new Map(prev.set(data.collaborationId, {
        status: data.status,
        // updatedAt: new Date(), // Remove this field as it doesn't exist in schema
      })));
    });

    return () => {
      unsubscribeUpdate();
      unsubscribeStatusChange();
    };
  }, [subscribeToMessage]);

  const filteredCollaborations = collaborations?.filter((collab: Collaboration) => {
    // Apply live updates if available
    const updatedCollab = liveUpdates.has(collab.id) 
      ? { ...collab, ...liveUpdates.get(collab.id) }
      : collab;

    const matchesSearch = updatedCollab.trackTitle.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         updatedCollab.initiatorName.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         updatedCollab.collaboratorName.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesStatus = selectedStatus === 'all' || updatedCollab.status === selectedStatus;
    const matchesRole = selectedRole === 'all' || updatedCollab.role === selectedRole;

    return matchesSearch && matchesStatus && matchesRole;
  });

  const handleJoinSession = () => {
    // Navigate to live collaboration session
    console.log('Joining live collaboration session...');
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <Users className="w-6 h-6 text-purple-400" />
          <h1 className="text-2xl font-bold text-white">Collaborations</h1>
          <Badge variant="secondary">{collaborations?.length || 0}</Badge>
        </div>

        {user && (user.role === 'artist' || user.role === 'producer') && (
          <Button
            data-testid="button-invite-collaboration"
            onClick={() => setShowCreateDialog(true)}
            className="bg-purple-500 hover:bg-purple-600"
          >
            <Plus className="w-4 h-4 mr-2" />
            Invite Collaborator
          </Button>
        )}
      </div>

      {/* View Toggle */}
      <div className="flex items-center space-x-4">
        <div className="flex space-x-1">
          <Button
            data-testid="button-view-my"
            variant={viewType === 'my' ? "default" : "outline"}
            size="sm"
            onClick={() => setViewType('my')}
          >
            My Collaborations
          </Button>
          <Button
            data-testid="button-view-all"
            variant={viewType === 'all' ? "default" : "outline"}
            size="sm"
            onClick={() => setViewType('all')}
          >
            All Collaborations
          </Button>
        </div>
      </div>

      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-4">
        {/* Search */}
        <div className="relative flex-1">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
          <Input
            data-testid="input-search-collaborations"
            placeholder="Search tracks or collaborators..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10 bg-dark-200 border-dark-400 text-white"
          />
        </div>

        {/* Status Filter */}
        <div className="flex items-center space-x-2">
          <Filter className="w-4 h-4 text-gray-400" />
          <div className="flex space-x-2">
            {STATUSES.map((status) => (
              <Button
                key={status}
                data-testid={`filter-status-${status}`}
                variant={selectedStatus === status ? "default" : "outline"}
                size="sm"
                onClick={() => setSelectedStatus(status)}
                className="text-xs"
              >
                {status === 'active' && <Zap className="w-3 h-3 mr-1" />}
                {status.charAt(0).toUpperCase() + status.slice(1)}
              </Button>
            ))}
          </div>
        </div>

        {/* Role Filter */}
        <div className="flex flex-wrap gap-2">
          {ROLES.map((role) => (
            <Button
              key={role}
              data-testid={`filter-role-${role}`}
              variant={selectedRole === role ? "default" : "outline"}
              size="sm"
              onClick={() => setSelectedRole(role)}
              className="text-xs"
            >
              {role.charAt(0).toUpperCase() + role.slice(1)}
            </Button>
          ))}
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {[
          { label: 'Pending', count: filteredCollaborations?.filter(c => c.status === 'pending').length || 0, color: 'text-yellow-400' },
          { label: 'Active', count: filteredCollaborations?.filter(c => c.status === 'active').length || 0, color: 'text-green-400' },
          { label: 'Completed', count: filteredCollaborations?.filter(c => c.status === 'completed').length || 0, color: 'text-blue-400' },
          { label: 'Total', count: filteredCollaborations?.length || 0, color: 'text-purple-400' },
        ].map((stat) => (
          <div key={stat.label} className="p-4 bg-dark-200 rounded-lg border border-dark-400">
            <p className="text-sm text-gray-400">{stat.label}</p>
            <p className={`text-2xl font-bold ${stat.color}`}>{stat.count}</p>
          </div>
        ))}
      </div>

      {/* Collaboration List */}
      <div className="space-y-4">
        {filteredCollaborations?.length === 0 ? (
          <div className="text-center py-12">
            <Users className="w-12 h-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-300 mb-2">No collaborations found</h3>
            <p className="text-gray-500">
              {searchTerm || selectedStatus !== 'all' || selectedRole !== 'all'
                ? 'Try adjusting your search or filters'
                : viewType === 'my'
                  ? 'Start collaborating with other artists!'
                  : 'No collaborations available right now.'
              }
            </p>
          </div>
        ) : (
          filteredCollaborations?.map((collaboration: Collaboration) => {
            // Apply live updates if available
            const updatedCollab = liveUpdates.has(collaboration.id) 
              ? { ...collaboration, ...liveUpdates.get(collaboration.id) }
              : collaboration;

            return (
              <CollaborationCard
                key={collaboration.id}
                collaboration={updatedCollab as Collaboration}
                onJoinSession={handleJoinSession}
              />
            );
          })
        )}
      </div>

      {/* Create Collaboration Dialog */}
      {showCreateDialog && (
        <CreateCollaborationDialog
          open={showCreateDialog}
          onOpenChange={setShowCreateDialog}
        />
      )}
    </div>
  );
}

client/src/components/collaborations/cre.content:
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { insertCollaborationSchema } from '@shared/schema';
import { useCreateCollaboration, useUsers, useTracks } from '@/hooks/useApi';
import { useAuth } from '@/hooks/useAuth';
import { Users, Music, MessageSquare } from 'lucide-react';
import type { InsertCollaboration } from '@shared/schema';

interface CreateCollaborationDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

const ROLES = [
  { value: 'featured', label: 'Featured Artist', desc: 'Main vocal or rap feature' },
  { value: 'producer', label: 'Producer', desc: 'Beat production and mixing' },
  { value: 'writer', label: 'Writer', desc: 'Lyrics and songwriting' },
  { value: 'lead', label: 'Lead Artist', desc: 'Primary artist on track' },
];

export default function CreateCollaborationDialog({ open, onOpenChange }: CreateCollaborationDialogProps) {
  const { user } = useAuth();
  const createCollaboration = useCreateCollaboration();
  const { data: users } = useUsers();
  const { data: tracks } = useTracks(user?.id);

  // Filter to artists and producers only
  const collaborators = users?.filter(u => 
    u.id !== user?.id && (u.role === 'artist' || u.role === 'producer')
  );

  // Filter to user's tracks only
  const myTracks = tracks?.filter(t => t.artistId === user?.id);

  const {
    register,
    handleSubmit,
    setValue,
    watch,
    reset,
    formState: { errors },
  } = useForm<InsertCollaboration>({
    resolver: zodResolver(insertCollaborationSchema),
    defaultValues: {
      initiatorId: user?.id || '',
      initiatorName: user?.displayName || '',
      // status: 'pending', // Remove this field as it's not in the form schema
    },
  });

  const selectedTrackId = watch('trackId');
  const selectedCollaboratorId = watch('collaboratorId');
  const selectedRole = watch('role');

  const selectedTrack = myTracks?.find(t => t.id === selectedTrackId);
  const selectedCollaborator = collaborators?.find(u => u.id === selectedCollaboratorId);

  const onSubmit = async (data: InsertCollaboration) => {
    try {
      // Set track title if a track is selected
      if (selectedTrack) {
        data.trackTitle = selectedTrack.title;
      }

      await createCollaboration.mutateAsync(data);
      reset();
      onOpenChange(false);
    } catch (error) {
      console.error('Failed to create collaboration:', error);
    }
  };

  const handleCollaboratorChange = (userId: string) => {
    const selectedUser = collaborators?.find(u => u.id === userId);
    if (selectedUser) {
      setValue('collaboratorId', userId);
      setValue('collaboratorName', selectedUser.displayName);
    }
  };

  const handleTrackChange = (trackId: string) => {
    const track = myTracks?.find(t => t.id === trackId);
    if (track) {
      setValue('trackId', trackId);
      setValue('trackTitle', track.title);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-2xl bg-dark-200 border-dark-400">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Users className="w-5 h-5 text-purple-400" />
            <span>Invite Collaborator</span>
          </DialogTitle>
          <DialogDescription>
            Invite another artist or producer to collaborate on your track
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit(onSubmit as any)} className="space-y-6">
          {/* Track Selection */}
          <div className="space-y-2">
            <Label htmlFor="track">Select Track*</Label>
            <Select onValueChange={handleTrackChange} data-testid="select-track">
              <SelectTrigger>
                <SelectValue placeholder="Choose a track to collaborate on" />
              </SelectTrigger>
              <SelectContent>
                {myTracks?.map((track) => (
                  <SelectItem key={track.id} value={track.id}>
                    <div className="flex items-center space-x-2">
                      <Music className="w-4 h-4" />
                      <span>{track.title}</span>
                      {track.genre && (
                        <Badge variant="outline" className="ml-2 text-xs">
                          {track.genre}
                        </Badge>
                      )}
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {errors.trackId && (
              <p className="text-red-400 text-sm">{errors.trackId.message}</p>
            )}
          </div>

          {/* Collaborator Selection */}
          <div className="space-y-2">
            <Label htmlFor="collaborator">Choose Collaborator*</Label>
            <Select onValueChange={handleCollaboratorChange} data-testid="select-collaborator">
              <SelectTrigger>
                <SelectValue placeholder="Select an artist or producer" />
              </SelectTrigger>
              <SelectContent>
                {collaborators?.map((collaborator) => (
                  <SelectItem key={collaborator.id} value={collaborator.id}>
                    <div className="flex items-center space-x-3">
                      <Avatar className="w-6 h-6">
                        <AvatarFallback className="text-xs">
                          {collaborator.displayName.charAt(0)}
                        </AvatarFallback>
                      </Avatar>
                      <span>{collaborator.displayName}</span>
                      <Badge variant="outline" className="ml-2">
                        {collaborator.role}
                      </Badge>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {errors.collaboratorId && (
              <p className="text-red-400 text-sm">{errors.collaboratorId.message}</p>
            )}
          </div>

          {/* Role Assignment */}
          <div className="space-y-3">
            <Label>Collaboration Role*</Label>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              {ROLES.map((role) => (
                <div
                  key={role.value}
                  className={`p-4 border rounded-lg cursor-pointer transition-all ${
                    selectedRole === role.value
                      ? 'border-purple-400 bg-purple-400/10'
                      : 'border-dark-400 hover:border-dark-300'
                  }`}
                  onClick={() => setValue('role', role.value)}
                  data-testid={`role-${role.value}`}
                >
                  <h4 className="font-medium text-white">{role.label}</h4>
                  <p className="text-sm text-gray-400 mt-1">{role.desc}</p>
                </div>
              ))}
            </div>
            {errors.role && (
              <p className="text-red-400 text-sm">{errors.role.message}</p>
            )}
          </div>

          {/* Description/Message */}
          <div className="space-y-2">
            <Label htmlFor="description">Collaboration Message</Label>
            <Textarea
              id="description"
              data-testid="input-collaboration-message"
              {...register('description')}
              placeholder="Tell them about your vision for this collaboration..."
              className="bg-dark-300 border-dark-400 min-h-[100px]"
              rows={4}
            />
            <p className="text-xs text-gray-500">
              Explain what you're looking for and how they can contribute to the track
            </p>
          </div>

          {/* Preview */}
          {selectedTrack && selectedCollaborator && selectedRole && (
            <div className="p-4 bg-dark-300 rounded-lg border border-dark-400">
              <h4 className="font-medium text-white mb-3 flex items-center">
                <MessageSquare className="w-4 h-4 mr-2" />
                Collaboration Preview
              </h4>
              <div className="space-y-2 text-sm">
                <p className="text-gray-300">
                  <span className="text-purple-400">Track:</span> {selectedTrack.title}
                </p>
                <p className="text-gray-300">
                  <span className="text-purple-400">Inviting:</span> {selectedCollaborator.displayName}
                </p>
                <p className="text-gray-300">
                  <span className="text-purple-400">Role:</span> {ROLES.find(r => r.value === selectedRole)?.label}
                </p>
              </div>
            </div>
          )}

          {/* Form Actions */}
          <div className="flex justify-end space-x-3">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              data-testid="button-cancel"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={createCollaboration.isPending || !selectedTrackId || !selectedCollaboratorId}
              className="bg-purple-500 hover:bg-purple-600"
              data-testid="button-send-invitation"
            >
              {createCollaboration.isPending ? 'Sending...' : 'Send Invitation'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/tracks/create-trac.content:
import { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Switch } from '@/components/ui/switch';
import { Badge } from '@/components/ui/badge';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { insertTrackSchema } from '@shared/schema';
import { useCreateTrack } from '@/hooks/useApi';
import { useAuth } from '@/hooks/useAuth';
import { useToast } from '@/hooks/use-toast';
import { Upload, Music, Loader2 } from 'lucide-react';
import type { InsertTrack } from '@shared/schema';

interface CreateTrackDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

const GENRES = ['Hip Hop', 'R&B', 'Pop', 'Electronic', 'Rock', 'Jazz', 'Country', 'Reggae'];

export default function CreateTrackDialog({ open, onOpenChange }: CreateTrackDialogProps) {
  const { user } = useAuth();
  const createTrack = useCreateTrack();
  const { toast } = useToast();
  const [isUploading, setIsUploading] = useState(false);
  const [isUploadingAudio, setIsUploadingAudio] = useState(false);
  const [isUploadingCover, setIsUploadingCover] = useState(false);

  const {
    register,
    handleSubmit,
    setValue,
    watch,
    reset,
    formState: { errors },
  } = useForm<InsertTrack>({
    resolver: zodResolver(insertTrackSchema),
    defaultValues: {
      artistId: user?.id || '',
      artistName: user?.displayName || '',
      isCollaborative: false,
      collaborators: [],
    },
  });

  const isCollaborative = watch('isCollaborative');

  const onSubmit = async (data: InsertTrack) => {
    try {
      setIsUploading(true);
      await createTrack.mutateAsync(data);
      reset();
      onOpenChange(false);
    } catch (error) {
      console.error('Failed to create track:', error);
    } finally {
      setIsUploading(false);
    }
  };

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploadingAudio(true);
    try {
      const formData = new FormData();
      formData.append('audio', file);

      const response = await fetch('/api/upload/audio', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const result = await response.json();
      setValue('audioUrl', result.url);

      // Extract duration from file
      const audio = new Audio();
      audio.src = URL.createObjectURL(file);
      audio.addEventListener('loadedmetadata', () => {
        setValue('duration', Math.floor(audio.duration));
        URL.revokeObjectURL(audio.src);
      });

      toast({
        title: 'Success',
        description: 'Audio file uploaded successfully',
      });
    } catch (error) {
      console.error('Audio upload failed:', error);
      toast({
        title: 'Upload Failed',
        description: 'Failed to upload audio file. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setIsUploadingAudio(false);
    }
  };

  const handleCoverUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsUploadingCover(true);
    try {
      const formData = new FormData();
      formData.append('image', file);

      const response = await fetch('/api/upload/image', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      const result = await response.json();
      setValue('coverImage', result.url);

      toast({
        title: 'Success',
        description: 'Cover image uploaded successfully',
      });
    } catch (error) {
      console.error('Cover upload failed:', error);
      toast({
        title: 'Upload Failed',
        description: 'Failed to upload cover image. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setIsUploadingCover(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md bg-dark-200 border-dark-400">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Music className="w-5 h-5 text-purple-400" />
            <span>Upload New Track</span>
          </DialogTitle>
          <DialogDescription>
            Share your music with the Viral Views community
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          {/* Audio File Upload */}
          <div className="space-y-2">
            <Label htmlFor="audioFile">Audio File*</Label>
            <div className="border-2 border-dashed border-dark-400 rounded-lg p-6 text-center">
              <Upload className="w-8 h-8 text-gray-400 mx-auto mb-2" />
              <input
                id="audioFile"
                type="file"
                accept="audio/*"
                onChange={handleFileUpload}
                className="hidden"
                data-testid="input-audio-file"
                disabled={isUploadingAudio}
              />
              <Label htmlFor="audioFile" className="cursor-pointer">
                {isUploadingAudio ? (
                  <div className="flex items-center space-x-2">
                    <Loader2 className="w-4 h-4 animate-spin" />
                    <span className="text-purple-400">Uploading audio...</span>
                  </div>
                ) : (
                  <>
                    <span className="text-purple-400 hover:text-purple-300">Choose audio file</span>
                    <p className="text-sm text-gray-500 mt-1">MP3, WAV, or M4A (max 100MB)</p>
                  </>
                )}
              </Label>
            </div>
          </div>

          {/* Track Details */}
          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="title">Title*</Label>
              <Input
                id="title"
                data-testid="input-track-title"
                {...register('title')}
                placeholder="Track title"
                className="bg-dark-300 border-dark-400"
              />
              {errors.title && (
                <p className="text-red-400 text-sm">{errors.title.message}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="genre">Genre</Label>
              <Select onValueChange={(value) => setValue('genre', value)}>
                <SelectTrigger data-testid="select-track-genre">
                  <SelectValue placeholder="Select genre" />
                </SelectTrigger>
                <SelectContent>
                  {GENRES.map((genre) => (
                    <SelectItem key={genre} value={genre}>
                      {genre}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="bpm">BPM</Label>
            <Input
              id="bpm"
              data-testid="input-track-bpm"
              {...register('bpm', { valueAsNumber: true })}
              type="number"
              placeholder="120"
              className="bg-dark-300 border-dark-400"
            />
          </div>

          {/* Cover Image Upload */}
          <div className="space-y-2">
            <Label htmlFor="coverImage">Cover Image</Label>
            <div className="border border-dark-400 rounded-lg p-4">
              <input
                id="coverImage"
                type="file"
                accept="image/*"
                onChange={handleCoverUpload}
                className="hidden"
                data-testid="input-cover-image"
                disabled={isUploadingCover}
              />
              <Label htmlFor="coverImage" className="cursor-pointer flex items-center space-x-2">
                {isUploadingCover ? (
                  <>
                    <Loader2 className="w-4 h-4 animate-spin text-gray-400" />
                    <span className="text-sm text-gray-300">Uploading cover...</span>
                  </>
                ) : (
                  <>
                    <Upload className="w-4 h-4 text-gray-400" />
                    <span className="text-sm text-gray-300">Upload cover art</span>
                  </>
                )}
              </Label>
            </div>
          </div>

          {/* Collaboration Settings */}
          <div className="flex items-center justify-between">
            <div className="space-y-0.5">
              <Label htmlFor="collaborative">Enable Collaboration</Label>
              <p className="text-sm text-gray-500">Allow other artists to collaborate on this track</p>
            </div>
            <Switch
              id="collaborative"
              data-testid="switch-collaborative"
              checked={isCollaborative || false}
              onCheckedChange={(checked) => setValue('isCollaborative', checked || false)}
            />
          </div>

          {/* Form Actions */}
          <div className="flex justify-end space-x-3">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              data-testid="button-cancel"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={isUploading || createTrack.isPending}
              data-testid="button-upload-track"
            >
              {isUploading || createTrack.isPending ? 'Uploading...' : 'Upload Track'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

